INSERT INTO `web_product` VALUES ('181', null, 'Xịt khoáng Grape Water', '280000', '0', '0', '0', '1', '', '1', '<p><span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35)\">Grape Water l&agrave; d&ograve;ng sản phẩm xịt kho&aacute;ng dạng phun sương c&oacute; kh&iacute; n&eacute;n của h&atilde;ng dược mỹ phẩm Caudalie (Ph&aacute;p). Sản phẩm được đựng trong chai kim loại c&oacute; nắp nhựa m&agrave;u trắng đục với hai thể t&iacute;ch 75ml tiện lợi cho việc mang theo khi di chuyển v&agrave; 200ml ph&ugrave; hợp để sử dụng tại nh&agrave; hoặc văn ph&ograve;ng.</span><br />\\r\\n<br />\\r\\n<span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35)\">- Sau khi d&ugrave;ng sữa rửa mặt v&agrave; nước hoa hồng, xịt một lớp Caudalie Grape Water trước c&aacute;c bước dưỡng kh&aacute;c để l&agrave;m s</span><span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35)\">ạch da tối đa, tạo điều kiện gi&uacute;p da hấp thụ tốt nhất c&aacute;c dưỡng chất.&nbsp;<br />\\r\\n<br />\\r\\n- Giữa giờ l&agrave;m việc hoặc khi vừa đi ra đường về, sử dụng sản phẩm n&agrave;y để dưỡng ẩm, l&agrave;m dịu m&aacute;t v&agrave; l&agrave;m sạch da.&nbsp;<br />\\r\\n<br />\\r\\n- Ngo&agrave;i ra, d&ugrave;ng Caudalie Grape Water để hỗ trợ qu&aacute; tr&igrave;nh trang điểm. Giữa mỗi lớp kem l&oacute;t, kem nền v&agrave; phấn phủ, t&ocirc;i đều sử dụng một lớp xịt kho&aacute;ng để gi&uacute;p da ăn phấn v&agrave; căng mọng hơn.</span></p>\\r\\n', '<p style=\"text-align:justify\"><strong><span style=\"font-size:10pt\">Th&ocirc;ng tin &amp; c&ocirc;ng dụng sản phẩm:</span></strong></p>\\r\\n\\r\\n<p style=\"text-align:justify\"><span style=\"font-size:10pt\">+ Th&agrave;nh phần chứa 100% nước nho v&agrave; bổ sung th&ecirc;m th&agrave;nh phần gi&agrave;u chất giữ ẩm Polysaccharide (th&agrave;nh phần tự nhi&ecirc;n của da c&oacute; t&aacute;c dụng như một t&aacute;c nh&acirc;n tốt trong việc giữ nước v&agrave; c&oacute; khả năng cao với đặc t&iacute;nh chống oxy ho&aacute;- nguồn: Paula&rsquo;s Choice) v&agrave; muối kho&aacute;ng.</span></p>\\r\\n\\r\\n<p style=\"text-align:justify\"><span style=\"font-size:10pt\">+ Chiết xuất 100% trực tiếp từ c&aacute;c quả nhỏ v&agrave;o m&ugrave;a thu hoạch, c&oacute; t&aacute;c dụng l&agrave;m dịu, m&aacute;t v&agrave; cung cấp độ ẩm cho da.</span></p>\\r\\n\\r\\n<p style=\"text-align:justify\"><span style=\"font-size:10pt\">+ Sản phẩm kh&ocirc;ng bao gồm: Parabens, Sulfates v&agrave; Phthalates.</span></p>\\r\\n\\r\\n<p style=\"text-align:justify\"><strong><span style=\"font-size:10pt\">- C&aacute;ch sử dụng (tham khảo):</span></strong></p>\\r\\n\\r\\n<p style=\"text-align:justify\"><span style=\"font-size:10pt\">+ Bạn c&oacute; thể xịt sau khi rửa mặt v&agrave; trước khi b&ocirc;i kem dưỡng ẩm. Một ch&uacute;t nước tr&ecirc;n da sẽ gi&uacute;p kem dưỡng ẩm thấm v&agrave;o da tốt hơn đấy nh&eacute; (giống như c&aacute;ch rửa mặt nhưng kh&ocirc;ng d&ugrave;ng khăn b&ocirc;ng để thấm nước m&agrave; để da kh&ocirc; tự nhi&ecirc;n vậy).</span></p>\\r\\n\\r\\n<p style=\"text-align:justify\"><span style=\"font-size:10pt\">+ Xịt sau khi trang điểm gi&uacute;p lớp trang điểm trong tự nhi&ecirc;n hơn.</span></p>\\r\\n\\r\\n<p style=\"text-align:justify\"><span style=\"font-size:10pt\">+ Xịt bất cứ l&uacute;c n&agrave;o khi bạn cảm thấy da cần th&ecirc;m độ ẩm trong ng&agrave;y (l&uacute;c n&agrave;o hơi buồn ngủ gật g&ugrave;, xịt l&ecirc;n da m&aacute;t m&aacute;t cũng gi&uacute;p bạn tỉnh t&aacute;o hơn khi phải l&agrave;m việc hoặc học h&agrave;nh qu&aacute; nhiều nh&eacute;).</span></p>\\r\\n\\r\\n<p style=\"text-align:justify\"><span style=\"font-size:10pt\"><strong>- Xuất xứ:&nbsp;</strong><span style=\"font-size:10pt\">H&atilde;ng dược mỹ phẩm</span>&nbsp;Caudalie Ph&aacute;p</span></p>\\r\\n', '01-09-35-29-04-2016-1093108012411100525807946831845290896266379n.jpg', '01-09-35-29-04-2016-1093108012411100525807946831845290896266379n.jpg', 'a:1:{i:0;s:68:\"01-09-35-29-04-2016-10931