INSERT INTO `web_product` VALUES ('191', null, 'BB Cream Baby Pink Nhật', '315000', '340000', '0', '0', '1', '', '1', '<p><span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35)\">BB Cream Baby Pink của Bison l&agrave; kem trang điểm được ưa chuộng nhất Nhật Bản m&agrave; bạn nhất định phải thử. BB Cream Baby Pink với chiết xuất tự nhi&ecirc;n gi&uacute;p đem lại cho bạn lớp trang điểm trắng s&aacute;ng, đẹp kh&ocirc;ng tỳ vết v&agrave; c&ograve;n dưỡng da mịn m&agrave;ng suốt cả ng&agrave;y. Phi&ecirc;n bản mới nhất 2015 với độ chống nắng vượt trội SPF 44, PA++++ bảo vệ v&agrave; l&agrave;m đẹp da suốt cả ng&agrave;y.</span><br />\\r\\n<span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35)\">D&ugrave;ng cho cả da nhạy cảm nhất</span></p>\\r\\n', '<p style=\"text-align:justify\"><span style=\"font-size:14px\">ếu l&agrave; một t&iacute;n đồ của mỹ phẩm Nhật th&igrave; bạn nhất định phải thử m&oacute;n&nbsp; &ldquo;đặc sản&rdquo;&nbsp;<span style=\"color:rgb(0, 128, 0)\"><em><strong>BB Cream Baby Pink &nbsp;</strong></em></span>v&ocirc; c&ugrave;ng nổi tiếng của nh&atilde;n hiệu Bison nh&eacute;. Ai d&ugrave;ng qua em n&agrave;y rồi th&igrave; đều biết ẻm tuyệt vời thế n&agrave;o nh&eacute;.</span></p>\\r\\n\\r\\n<p style=\"text-align:justify\"><br />\\r\\n<span style=\"font-size:14px\"><img alt=\"heart\" height=\"20\" src=\"http://www.myphamnhatcaocap.com/ckeditor/plugins/smiley/images/heart.gif\" title=\"heart\" width=\"20\" />Về th&agrave;nh phần th&igrave; Baby Pink được chiết xuất ch&iacute;nh từ kho&aacute;ng chất bổ sung tinh chất Witch Hazel chiết xuất từ quả bưởi ho&agrave;n to&agrave;n kh&ocirc;ng chứa ch&igrave;, h&oacute;a chất v&agrave; cả chất tạo m&ugrave;i.</span></p>\\r\\n\\r\\n<p style=\"text-align:justify\">&nbsp;</p>\\r\\n\\r\\n<p style=\"text-align:center\"><img alt=\"bb-cream-baby-pink\" src=\"http://www.myphamnhatcaocap.com/kcfinder/upload/images/bb-cream-baby-pink-2%20%282%29.jpg\" title=\"BB Cream Baby Pink \