INSERT INTO `web_product` VALUES ('192', null, 'April Skin Magic Snow', '410000', '0', '0', '0', '1', '', '1', '<p><span style=\"background-color:rgb(255, 255, 255); color:rgb(20, 24, 35)\">April Skin Magic Snow l&agrave; &quot;l&iacute;nh mới&quot; trong d&ograve;ng phấn nước được sản xuất tại H&agrave;n Quốc. Xuất hiện v&agrave;o nửa cuối năm 2015, em April Skin n&agrave;y đ&atilde; tạo ra cơn sốt kh&ocirc;ng hề nhỏ v&agrave; lọt v&agrave;o danh s&aacute;ch top mỹ phẩm b&aacute;n chạy tại H&agrave;n Quốc.2 t&ocirc;ng phổ biến của d&ograve;ng phấn n&agrave;y l&agrave; m&agrave;u be s&aacute;ng 21 v&agrave; m&agrave;u hồng be 22. M&agrave;u be s&aacute;ng gi&uacute;p da trắng hơn th&iacute;ch hợp cho c&ocirc; n&agrave;ng n&agrave;o da tối v&agrave; sẫm m&agrave;u. M&agrave;u hồng be th&iacute;ch hợp cho n&agrave;ng n&agrave;o da trắng, ph&ugrave; hợp với lối trang điểm tự nhi&ecirc;n, trong suốt.</span></p>\\r\\n', '<p><strong><a href=\"https://xachtaynhat.net/products/phan-nuoc-cushion\" style=\"margin: 0px; padding: 0px; border: 0px; font-weight: inherit; font-style: inherit; vertical-align: baseline; text-decoration: none; color: rgb(154, 102, 20);\">Phấn Nước &nbsp;Cushion</a>&nbsp;</strong>l&agrave; sản phẩm rất được ưa chuộng từ thương hiệu mỹ phẩm th&acirc;n thiện với sức khỏe nổi tiếng của H&agrave;n Quốc &ndash; Aprilskin. Một si&ecirc;u phẩm trong t&uacute;i đồ trang điểm của bạn .Với c&ocirc;ng dụng 3 in 1 , vừa l&agrave;m kem nền,kem che khuyết điểm vừa l&agrave; phấn phủ .<strong>Phấn Nước Cushion</strong>&nbsp;sẽ mang lại cho bạn một l&agrave;n da mịn m&agrave;g trắng s&aacute;ng tự nhi&ecirc;n sau khi trang điểm.</p>\\r\\n\\r\\n<p>.<a href=\"https://xachtaynhat.net/wp-content/uploads/2016/01/phan-nuoc-Cushion-han-quoc.jpg\" style=\"margin: 0px; padding: 0px; border: 0px; font-weight: inherit; font-style: inherit; vertical-align: baseline; text-decoration: none; color: rgb(154, 102, 20);\"><img alt=\"phan nuoc Cushion han quoc tot nhat\" height=\"640\"