INSERT INTO `web_product` VALUES ('208', null, 'Kem ủ Garnier Ultra Doux tinh chất bơ', '245000', '0', '0', '0', '1', '', '1', '<p><strong>Kem ủ Garnier Ultra Doux tinh chất bơ cho t&oacute;c mềm mượt, b&oacute;ng khỏe</strong></p>\\r\\n\\r\\n<p>Nếu bạn vẫn lu&ocirc;n lo lắng về m&aacute;i t&oacute;c xơ rối, chẻ ngọn v&agrave; hư tổn nặng th&igrave; nay c&oacute; thể y&ecirc;n t&acirc;m khi c&oacute; kem ủ&nbsp;Garnier Ultra Doux&nbsp;tinh chất bơ, gi&uacute;p t&oacute;c&nbsp;mềm mượt, bổ sung dưỡng chất phục hồi cho t&oacute;c kh&ocirc; bị hư tổn, phục hồi t&oacute;c chẻ ngọn</p>\\r\\n', '<p style=\"text-align:center\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:18pt\"><strong>Kem ủ Garnier Ultra Doux tinh chất bơ cho t&oacute;c mềm mượt, b&oacute;ng khỏe</strong></span></p>\\r\\n\\r\\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:11pt\">Nếu bạn vẫn lu&ocirc;n lo lắng về m&aacute;i t&oacute;c xơ rối, chẻ ngọn v&agrave; hư tổn nặng th&igrave; nay c&oacute; thể y&ecirc;n t&acirc;m khi c&oacute; kem ủ&nbsp;<a href=\"http://chiaki.vn/kem-u-garnier-ultra-doux-tinh-chat-bo-cho-toc-mem-muot-bong-khoe\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(51, 122, 183); text-decoration: none; background-color: transparent;\" title=\"Garnier Ultra Doux\">Garnier Ultra Doux</a>&nbsp;tinh chất bơ, gi&uacute;p t&oacute;c&nbsp;mềm mượt, bổ sung dưỡng chất phục hồi cho t&oacute;c kh&ocirc; bị hư tổn, phục hồi t&oacute;c chẻ ngọn</span></p>\\r\\n\\r\\n<p><span style=\"font-family:arial,helvetica,sans-serif; font-size:11pt\"><img alt=\"Kem ủ Garnier Ultra Doux tinh chất bơ\" src=\"http://chiaki.vn/upload/news/content/2016/02/u-garnier-ultra-doux-tinh-chat-bo3-png-1454488131-03022016152851.png\" width=\"450\" /></span></p>\\r\\n\\r\\n<p style=\"text-align:center\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:10pt\"><em>Kem ủ Garnier Ultra Doux tinh chất bơ cho t&oacute;c mềm mư