INSERT INTO `web_product` VALUES ('224', null, 'Điện thoại OPPO Neo 5 -R1201', '0', '0', '0', '0', '2', 'Bảo hành chính hãng, phụ kiện đầy đủ', '1', '<p>Chipset: MT6582 Quad-core 1.3 GHz</p>\\r\\n\\r\\n<p>RAM 1 GB/ Bộ nhớ trong 8 GB</p>\\r\\n\\r\\n<p>M&agrave;n h&igrave;nh: 4.5 inch, 854 x 480 pixels</p>\\r\\n\\r\\n<p>Camera: 8.0 MP/ 2.0 MP</p>\\r\\n\\r\\n<p>Dung lượng pin 2000 mAh</p>\\r\\n', '<h2><span style=\"font-size:medium\"><strong>Điện thoại di động OPPO Neo 5 -R1201</strong></span></h2>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">Thiết kế 2 mặt gương</span></strong></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\"><img alt=\"\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo-neo-5.jpg\" /></span></strong></p>\\r\\n\\r\\n<p><em><strong><span style=\"font-size:small\">Sang trọng c&ugrave;ng hai m&agrave;u thời trang</span></strong></em></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Quy tr&igrave;nh d&aacute;t mỏng độc đ&aacute;o gi&uacute;p phần nắp lưng của OPPO Neo 5 trở n&ecirc;n mỏng hơn, s&aacute;ng hơn v&agrave; tự nhi&ecirc;n hơn. Mặt lưng b&oacute;ng bẩy c&ugrave;ng khả năng uốn dẻo linh hoạt kh&ocirc;ng những gi&uacute;p mặt lưng Neo 5 trở n&ecirc;n s&aacute;ng b&oacute;ng, sang trọng m&agrave; c&ograve;n c&oacute; chất lượng bền bỉ. </span><br />\\r\\n<span style=\"font-size:small\">Sản phẩm c&oacute; hai phi&ecirc;n bản m&agrave;u l&agrave; trắng pha l&ecirc; v&agrave; xanh sapphire.</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">Độ bền vượt trội</span></strong></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\"><img alt=\"\" height=\"337\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo-neo-5-1.jpg\" width=\"900\" /></span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Khung m&aacute;y Neo 5 được trang bị cấu tr&uacute;c hai lớp kim loại, tạo n&ecirc;n một th&acirc;n m&aacute;y mỏng, liền khối nhưng rất chắc chắn. So với cấu tr&uacute;c một lớp kim loại th&ocirc;ng thường, Neo 5 sở hữu th&acirc;n m&aacute;y cứng c&aacute;p v&agrave; hiệu quả tản nhiệt được n&acirc;ng cao hơn.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:small\"><img alt=\"\" height=\"565\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo-neo-5-2.jpg\" width=\"661\" /></span></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Khung m&aacute;y Neo 5 được trang bị cấu tr&uacute;c hai lớp kim loại, tạo n&ecirc;n một th&acirc;n m&aacute;y mỏng, liền khối nhưng rất chắc chắn. So với cấu tr&uacute;c một lớp kim loại th&ocirc;ng thường, Neo 5 sở hữu th&acirc;n m&aacute;y cứng c&aacute;p v&agrave; hiệu quả tản nhiệt được n&acirc;ng cao hơn.</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">T&ugrave;y chọn icon chụp ảnh- C</span><span style=\"font-size:small\">hiếc m&aacute;y ảnh t&ugrave;y biến của ri&ecirc;ng bạn</span></strong></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\"><img alt=\"\" height=\"711\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo-neo-5-3.jpg\" width=\"493\" /></span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">C&ocirc;ng nghệ tạo ảnh PI (Pure Image) 2.0+ mang theo một sự đa dạng về c&aacute;c tiện &iacute;ch hỗ trợ chức năng của camera. C&aacute;c tiện &iacute;ch ho&agrave;n to&agrave;n c&oacute; thể t&ugrave;y biến th&ecirc;m bớt t&ugrave;y v&agrave;o sở th&iacute;ch v&agrave; nhu cầu của bạn. H&atilde;y biến chiếc camera trở n&ecirc;n độc đ&aacute;o theo c&aacute;ch của ri&ecirc;ng bạn, bằng c&aacute;c chức năng tự sướng, chụp ảnh nghệ thuật, quay phim hay thậm ch&iacute; tạo ra c&aacute;c bức ảnh ngộ nghĩnh.</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">Phơi s&aacute;ng k&eacute;p&nbsp;</span><span style=\"font-size:small\">Bất ngờ đầy th&uacute; vị</span></strong></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\"><img alt=\"\" height=\"544\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo-neo-5-4.jpg\" width=\"900\" /></span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Sử dụng thủ thuật chồng nền hai tấm ảnh để ảnh &ldquo;tả thực&rdquo; trở n&ecirc;n nghệ thuật, lung linh huyền ảo đến kinh ngạc.</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">Beautify 3.0&nbsp;</span><span style=\"font-size:small\">Để thấy bạn đẹp hơn</span></strong></p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\"><img alt=\"\" height=\"719\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo-neo-5-6.jpg\" width=\"713\" /></span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Mỗi chế độ chụp ảnh đều c&oacute; phong c&aacute;ch v&agrave; m&agrave;u sắc ri&ecirc;ng. Beautify 3.0 c&oacute; 8 chế độ chụp, để mỗi ng&agrave;y l&agrave; một niềm vui mới</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">Smart Dual SIM</span></strong></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\"><img alt=\"\" height=\"583\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo-neo-5-7.jpg\" width=\"900\" /></span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">OPPO Neo 5 được trang bị 2 khe cắm SIM với chế độ Smart Dual để bạn kh&ocirc;ng bỏ lỡ bất kỳ cuộc gọi quan trọng n&agrave;o. </span><br />\\r\\n<span style=\"font-size:small\">Khe cắm thẻ nhớ ngo&agrave;i hỗ trợ l&ecirc;n tới 32GB, mở rộng kh&ocirc;ng gian lưu trữ của bạn hơn bao giờ hết.</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">Chip 4 nh&acirc;n mạnh mẽ, RAM 1 GB mượt m&agrave;&nbsp;</span><span style=\"font-size:small\">Nhanh hơn v&agrave; mượt hơn</span></strong></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\"><img alt=\"\" height=\"439\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo-neo-5-8.jpg\" width=\"900\" /></span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Được trang bị vi xử l&yacute; l&otilde;i tứ c&ugrave;ng 1GB RAM, OPPO Neo 5 hoạt động với hiệu suất cao hơn v&agrave; mức ti&ecirc;u thụ năng lượng thấp hơn, cho bạn những trải nghiệm nhanh, mượt v&agrave; độc đ&aacute;o.</span></p>\\r\\n', '03-51-45-30-04-2016-201505061516181494opponeo5656.jpg', '03-51-52-30-04-2016-oppo-neo-5.jpg', 'a:3:{i:0;s:53:\"03-51-45-30-04-2016-201505061516181494opponeo5656.jpg\";i:1;s:34:\"03-51-52-30-04-2016-oppo-neo-5.jpg\";i:2;s:36:\"03-51-52-30-04-2016-oppo-neo-5-6.jpg\";}', '0', null, null, '44', 'Điện thoại', '0', '0', '1', '1', '16', 'Quang Huy Mobile', '1', '1', null, '0', '22', null, null, '1462006305', null, null, '1478313253');
INSERT INTO `web_product` VALUES ('225', null, 'Điện thoại di động OPPO R7 Lite - R7K', '0', '0', '0', '0', '2', 'Bảo hành chính hãng, phụ kiện đầy đủ', '1', '<p>M&agrave;n h&igrave;nh:&nbsp;AMOLED 16 triệu m&agrave;u, 5.0&quot;, 720 x 1280 pixels</p>\\r\\n\\r\\n<p>Hệ điều h&agrave;nh:&nbsp;Android 5.1</p>\\r\\n\\r\\n<p>UICamera: Camera trước 8 megapixels, camera sau 13MP&nbsp;</p>\\r\\n\\r\\n<p>Ram: 2G</p>\\r\\n', '<h2><strong>Điện thoại di động OPPO R7 Lite - R7KF</strong></h2>\\r\\n\\r\\n<p><strong>OPPO R7 Lite</strong>&nbsp;l&agrave; chiếc smartphone thế hệ thứ tư của d&ograve;ng R v&agrave; được xem như người anh em song sinh của OPPO R7, d&ograve;ng điện thoại thời trang của OPPO với nhiều cải tiến về cả thiết kế, hiệu năng v&agrave; đặc biệt l&agrave; khả năng chụp ảnh. Để bạn kh&ocirc;ng chỉ c&oacute; một chiếc smartphone với những t&iacute;nh năng tuyệt đỉnh m&agrave; c&ograve;n c&oacute; trong tay cả một m&oacute;n đồ thời trang cao cấp.</p>\\r\\n\\r\\n<h3><img alt=\"thiet-ke-OPPO-R7\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo/r7-lite/OPPO-R7-1.jpg\" width=\"800\" /></h3>\\r\\n\\r\\n<h3>&nbsp;</h3>\\r\\n\\r\\n<h3>Oppo R7 Lite sở hữu thiết kế kim loại đỉnh cao, ho&agrave;n hảo trong l&ograve;ng b&agrave;n tay</h3>\\r\\n\\r\\n<p><strong>OPPO R7&nbsp;Lite</strong>&nbsp;được thiết kế kim loại nguy&ecirc;n khối v&agrave; l&agrave; một trong những smartphone c&oacute; tỉ lệ ho&agrave;n thiện bằng kim loại cao nhất thế giới hiện nay. To&agrave;n th&acirc;n mỗi chiếc m&aacute;y&nbsp;R7&nbsp;Lite&nbsp;được chế từ một miếng hợp kim magie nh&ocirc;m duy nhất v&agrave; trải qua 48 quy tr&igrave;nh cắt, r&aacute;p, đ&aacute;nh b&oacute;ng để tạo n&ecirc;n một chiếc m&aacute;y liền lạc, bền vững, v&ocirc; c&ugrave;ng sang trọng. Tỷ lệ kim loại tr&ecirc;n&nbsp;<strong>OPPO R7 Lite</strong>&nbsp;l&agrave; 92,3%. Sự mạnh mẽ của kim loại chắc chắn sẽ hấp dẫn bạn ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n.</p>\\r\\n\\r\\n<h3><img alt=\"OPPO R7 thiet ke\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo/r7-lite/OPPO-R7-5.jpg\" width=\"800\" /></h3>\\r\\n\\r\\n<p>Nếu như người tiền nhiệm OPPO R5 tập trung thiết kế về độ mỏng (4,85mm) th&igrave; OPPO R7 Lite lại ch&uacute; &yacute; nhiều hơn về trải nghiệm người d&ugrave;ng. Vẫn sở hữu một độ mỏng xuất sắc 6,3mm nhưng OPPO R7 Lite cho người d&ugrave;ng cảm gi&aacute;c cầm rất tuyệt vời.&nbsp;Điện thoại OPPO R7 Lite&nbsp;được&nbsp;OPPO&nbsp;thiết kế c&aacute;c mặt bo tr&ograve;n cổ điển, thon gọn v&agrave; h&ograve;a hợp v&agrave;o l&ograve;ng b&agrave;n tay. Những đường cắt kim cương s&aacute;ng b&oacute;ng đẹp mắt kh&ocirc;ng chỉ trang tr&iacute; m&agrave; n&oacute; cũng khiến bạn cầm m&aacute;y được dễ d&agrave;ng, &ecirc;m &aacute;i hơn.</p>\\r\\n\\r\\n<h3><img alt=\"OPPO R7 hands on\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo/r7-lite/OPPO-R7-3.jpg\" width=\"800\" /></h3>\\r\\n\\r\\n<h3>&nbsp;</h3>\\r\\n\\r\\n<h3>Điện thoại Oppo R7 Lite được trang bị m&agrave;n h&igrave;nh 5 inch Super AMOLED HD, c&ocirc;ng nghệ 2,5D</h3>\\r\\n\\r\\n<p>OPPO đ&atilde; tạo ra một kh&aacute;i niệm mới về m&agrave;n h&igrave;nh tr&ecirc;n&nbsp;đi&ecirc;̣n thoại OPPO R7&nbsp;Lite&nbsp;khi m&aacute;y sở hữu m&agrave;n h&igrave;nh c&ocirc;ng nghệ cong 2,5D độc đ&aacute;o. Kh&ocirc;ng phải l&agrave; một m&agrave;n h&igrave;nh cong thật sự nhưng m&agrave;n h&igrave;nh<strong>&nbsp;</strong>OPPO R7 Lite&nbsp;liền lạc ho&agrave;n to&agrave;n với mặt trước để chất lượng h&igrave;nh ảnh tốt hơn v&agrave; nổi l&ecirc;n trước mắt bạn. Bạn phải thật sự cầm m&aacute;y mới c&oacute; thể thấy được độ nổi hấp dẫn của m&agrave;n h&igrave;nh R7.&nbsp;<strong>M&agrave;n h&igrave;nh OPPO R7 Lite</strong>&nbsp;c&oacute; k&iacute;ch thước 5 inch độ ph&acirc;n giải HD cho h&igrave;nh ảnh sắc n&eacute;t. Kh&ocirc;ng chỉ vậy, c&ocirc;ng nghệ Super AMOLED gi&uacute;p m&agrave;n h&igrave;nh tiết kiệm pin v&agrave; hiển thị m&agrave;u sắc sống động, rực rỡ hơn. Bạn sẽ c&oacute; những trải nghiệm tuyệt vời tr&ecirc;n m&agrave;n h&igrave;nh cảm ứng si&ecirc;u nhạy&nbsp;OPPO R7 Lite.</p>\\r\\n\\r\\n<h3><img alt=\"OPPO R7 man hinh\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo/r7-lite/OPPO-R7-9.jpg\" width=\"800\" /></h3>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<h3>Camera ch&iacute;nh của Oppo R7 Lite l&ecirc;n tới 13MP chất lượng Ultra-HD</h3>\\r\\n\\r\\n<p>Bạn sẽ c&oacute; những h&igrave;nh ảnh chi tiết nhất nhờ camera ch&iacute;nh 13MP c&ocirc;ng nghệ ISOCELL của OPPO R7 Lite. Cảm biến xử l&yacute; h&igrave;nh ảnh mới nhất c&ugrave;ng phần mềm ưu việt gi&uacute;p cho mỗi bức ảnh OPPO R7 Lite chụp được đều c&oacute; chiều s&acirc;u, chất lượng ho&agrave;n hảo. Bạn kh&ocirc;ng cần phải biết qu&aacute; nhiều về nhiếp ảnh, tất cả bạn cần chỉ l&agrave; giơ m&aacute;y l&ecirc;n v&agrave; chụp khi&nbsp;<strong>OPPO R7&nbsp;Lite</strong>&nbsp;c&oacute; thể tự động ph&aacute;t hiện lấy n&eacute;t, tối ưu h&oacute;a chống rung v&agrave; tốc độ chụp cực nhanh để bạn ngay lập tức c&oacute; một bức ảnh ưng &yacute; nhất.</p>\\r\\n\\r\\n<h3><img alt=\"OPPO R7 camera\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo/r7-lite/OPPO-R7-10.jpg\" width=\"800\" /></h3>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<h3>Camera trước độ ph&acirc;n giải cao 8MP &ndash; bạn l&agrave; người đẹp nhất</h3>\\r\\n\\r\\n<p><strong>OPPO R7 Lite</strong>&nbsp;xứng đ&aacute;ng l&agrave; một trong những smartphone c&oacute; camera trước chụp ch&acirc;n dung đẹp nhất tr&ecirc;n thị trường khi sở hữu camera trước độ ph&acirc;n giải 8MP ống k&iacute;nh OmniVision OV8858. Bạn sẽ nh&igrave;n thấy m&igrave;nh thật lung linh sau khi &ldquo;chụp ảnh tự sướng&rdquo; tr&ecirc;n&nbsp;<strong>OPPO R7 Lite</strong>. Với&nbsp;OPPO R7 Lite&nbsp;bạn sẽ th&ecirc;m y&ecirc;u bản th&acirc;n khi thấy h&igrave;nh ảnh của m&igrave;nh lu&ocirc;n đẹp v&agrave; bừng s&aacute;ng.</p>\\r\\n\\r\\n<h3><img alt=\"OPPO R7 front cam\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo/r7-lite/OPPO-R7-7.jpg\" width=\"800\" /></h3>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<h3>Oppo R7 Lite mạnh mẽ với bộ vi xử l&yacute; 8 l&otilde;i, 2GB RAM</h3>\\r\\n\\r\\n<p><strong>OPPO R7&nbsp;Lite</strong>&nbsp;được trang bị bộ vi xử l&yacute; t&aacute;m l&otilde;i Octa Core Qualcomm Snapdragon 615&nbsp;hoạt động c&ugrave;ng xung nhịp 1,3 GHz cho tốc độ xử l&yacute; cực nhanh. Nh&acirc;n đồ họa GPU Adreno 405 đủ sức xử l&yacute; h&igrave;nh ảnh của những game 3D phức tạp hay c&aacute;c bộ phim Full HD tốc độ khung h&igrave;nh cao. Trong khi đ&oacute;,&nbsp;bộ nhớ RAM 2GB, cho ph&eacute;p bạn thỏa sức mở ứng dụng thoải m&aacute;i m&agrave; kh&ocirc;ng bị lag hay giật.&nbsp;OPPO R7 Lite c&oacute; bộ nhớ trong 16GB v&agrave; hỗ trợ thẻ nhớ l&ecirc;n đến 128GB, do đ&oacute; bạn c&oacute; thể mở rộng th&ecirc;m kh&ocirc;ng gian lưu trữ một c&aacute;ch dễ d&agrave;ng.</p>\\r\\n', '03-55-49-30-04-2016-oppor7.png', '03-55-49-30-04-2016-oppo-r7-lite-r7kf-16gb-gold-9011-9324952-1-zoom.jpg', 'a:3:{i:0;s:51:\"03-55-42-30-04-2016-21342oppor7liter7kfsliver01.jpg\";i:1;s:71:\"03-55-49-30-04-2016-oppo-r7-lite-r7kf-16gb-gold-9011-9324952-1-zoom.jpg\";i:2;s:30:\"03-55-49-30-04-2016-oppor7.png\";}', '0', null, null, '44', 'Điện thoại', '0', '0', '1', '1', '16', 'Quang Huy Mobile', '1', '1', null, '0', '22', null, null, '1462006541', null, null, '1478313253');
INSERT INTO `web_product` VALUES ('226', null, 'Điện thoại di động OPPO JOY 3 - A11', '0', '0', '0', '0', '2', 'Bảo hành chính hãng, phụ kiện đầy đủ', '1', '<p>Chipset :&nbsp;MediaTek MT6582M</p>\\r\\n\\r\\n<p>M&agrave;n h&igrave;nh : 4.5 inch, 480 x 854 pixels</p>\\r\\n\\r\\n<p>Camera : 5.0 MP/ 2.0 MP</p>\\r\\n', '<div class=\"std\">\\r\\n<h2><span style=\"font-size:medium\"><strong>Điện thoại di động OPPO JOY 3 - A11wW</strong></span></h2>\\r\\n\\r\\n<p><strong>Oppo Joy 3</strong>&nbsp;l&agrave; chiếc smartphone c&oacute; thiết kế đẹp, m&agrave;n h&igrave;nh sắc n&eacute;t, bộ vi xử l&yacute; l&otilde;i tứ mạnh mẽ c&ugrave;ng bộ đ&ocirc;i camera chất lượng v&agrave; rất nhiều t&iacute;nh năng th&uacute; vị kh&aacute;c. H&atilde;y c&ugrave;ng kh&aacute;m ph&aacute; v&agrave; tận hưởng những trải nghiệm mới mẻ m&agrave; chiếc smartphone n&agrave;y mang lại.</p>\\r\\n\\r\\n<p><img alt=\"Oppo Joy 3\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo/joy-3/oppo_joy_3.png\" /></p>\\r\\n\\r\\n<h2><strong>Thiết kế hiện đại, năng động</strong></h2>\\r\\n\\r\\n<p><strong>Oppo Joy 3</strong>&nbsp;tạo được ấn tượng bởi thiết kế vu&ocirc;ng vắn với những đường n&eacute;t sắc sảo c&ugrave;ng phong c&aacute;ch trẻ trung, hiện đại. C&aacute;c cạnh được v&aacute;t thẳng nhưng đến c&aacute;c g&oacute;c lại được bo tr&ograve;n mềm mại tạo cho m&aacute;y một vẻ đẹp vừa mạnh mẽ lại vừa thanh tho&aacute;t. Kiểu d&aacute;ng kh&aacute; mỏng gọn gi&uacute;p m&aacute;y &ocirc;m s&aacute;t v&agrave;o l&ograve;ng b&agrave;n tay, cho cảm gi&aacute;c cầm nắm dễ d&agrave;ng v&agrave; chắc chắn như một chiếc điện thoại nguy&ecirc;n khối cao cấp. Đặc biệt, Oppo Joy 3 c&ograve;n được trang bị một lớp viền &aacute;nh kim chạy dọc th&acirc;n m&aacute;y gi&uacute;p m&aacute;y trở n&ecirc;n nổi bật v&agrave; sang trọng hơn. Với hai phi&ecirc;n bản m&agrave;u sắc ch&iacute;nh l&agrave; trắng thanh lịch v&agrave; đen lịch l&atilde;m,&nbsp;<strong>Oppo Joy 3</strong>&nbsp;ph&ugrave; hợp với mọi đối tượng sử dụng, cả ph&aacute;i nam v&agrave; ph&aacute;i nữ.</p>\\r\\n\\r\\n<h2><strong>Oppo Joy 3 với m&agrave;n h&igrave;nh IPS 4.5 inch s&aacute;ng đẹp, sống động</strong></h2>\\r\\n\\r\\n<p><strong>Oppo Joy 3</strong>&nbsp;mang đến bạn những trải nghiệm hết sức th&uacute; vị nhờ được trang bị m&agrave;n h&igrave;nh 4.5 inch sắc n&eacute;t. Độ ph&acirc;n giải 480 x 854 pixels kết hợp c&ugrave;ng c&ocirc;ng nghệ IPS ti&ecirc;n tiến sẽ mang đến bạn những h&igrave;nh ảnh s&aacute;ng đẹp, đầy đủ chi tiết, sống động với 16 triệu m&agrave;u sắc v&agrave; g&oacute;c nh&igrave;n rộng, rất tuyệt vời cho việc giải tr&iacute;, gi&uacute;p bạn tận hưởng c&aacute;c bộ phim Full HD hay chơi c&aacute;c game 3D hấp dẫn một c&aacute;ch trọn vẹn nhất.</p>\\r\\n\\r\\n<p><img alt=\"Oppo Joy 3\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo/joy-3/oppo_joy_3_a.png\" /></p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<h2><strong>Hiệu năng mượt m&agrave;</strong></h2>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p>B&ecirc;n trong&nbsp;<strong>Oppo Joy 3&nbsp;</strong>l&agrave; bộ vi xử l&yacute; l&otilde;i tứ MT6582M xung nhịp 1.3GHz của MediaTek cho tốc độ xử l&yacute; nhanh ch&oacute;ng trong mọi ho&agrave;n cảnh. Bạn c&oacute; thể thoải m&aacute;i lướt web, kiểm tra email, đọc văn bản hay chơi cả những game 3D một c&aacute;ch mượt m&agrave;. Bộ nhớ RAM 1GB đảm bảo cho khả năng đa nhiệm hoạt động tốt, để bạn c&oacute; thể trải nghiệm nhiều ứng dụng c&ugrave;ng l&uacute;c. Chip đồ họa Mali 400MP2 cho ph&eacute;p m&aacute;y chạy được cả những ứng dụng đ&ograve;i hỏi khả năng xử l&yacute; đồ họa cao v&agrave; xem những bộ phim chất lượng cao. Cuối c&ugrave;ng l&agrave; bộ nhớ trong 4GB c&oacute; thể mở rộng th&ecirc;m bằng khe cắm thẻ nhớ ngo&agrave;i l&ecirc;n đến 32GB, cho ph&eacute;p bạn thoải m&aacute;i lưu trữ b&agrave;i h&aacute;t, ảnh, game hay bất cứ điều g&igrave; m&igrave;nh muốn.</p>\\r\\n\\r\\n<p><img alt=\"Oppo Joy 3\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo/joy-3/oppo_joy_3_d.jpg\" /></p>\\r\\n\\r\\n<h2><strong>Camera ch&iacute;nh 5MP, camera trước 2MP</strong></h2>\\r\\n\\r\\n<p><strong>Điện thoại Oppo Joy 3</strong>&nbsp;được trang bị đầy đủ hai camera trước v&agrave; sau để gi&uacute;p bạn lưu lại mọi khoảnh khắc đ&aacute;ng nhớ b&ecirc;n gia đ&igrave;nh, bạn b&egrave; bất cứ l&uacute;c n&agrave;o. Camera ch&iacute;nh 5MP c&oacute; đ&egrave;n flash LED trợ s&aacute;ng c&ugrave;ng những phần mềm chỉnh sửa ảnh của Oppo sẽ mang đến cho bạn những bức ảnh chất lượng cả về m&agrave;u săc lẫn sự chi tiết. Trong khi đ&oacute;, camera trước của m&aacute;y cũng rất được Oppo ch&uacute; trọng khi c&oacute; độ ph&acirc;n giải đến 2MP, hứa hẹn sẽ cho bạn những bức h&igrave;nh chụp selfie cực đẹp v&agrave; những cuộc đ&agrave;m thoại video chất lượng cao.</p>\\r\\n\\r\\n<p><img alt=\"Oppo Joy 3\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/oppo/joy-3/oppo_joy_3_b.png\" /></p>\\r\\n\\r\\n<h2><strong>Tận hưởng niềm vui v&agrave; sự tiện dụng từ Color OS tr&ecirc;n Oppo Joy 3</strong></h2>\\r\\n\\r\\n<p>T&iacute;nh năng kinh điển tr&ecirc;n Color OS 2.0 tiếp tục được Oppo duy tr&igrave; v&agrave; ph&aacute;t triển tr&ecirc;n&nbsp;<strong>Oppo Joy 3</strong>&nbsp;tạo sự kh&aacute;c biệt ho&agrave;n to&agrave;n so với c&aacute;c m&aacute;y Android hiện đang c&oacute; mặt tr&ecirc;n thị trường. Kh&ocirc;ng chỉ đẹp v&agrave; khiến bạn th&iacute;ch th&uacute; với hiệu ứng h&igrave;nh nền thời tiết sinh động m&agrave; Color OS c&ograve;n mang đến bạn rất nhiều tiện &iacute;ch th&uacute; vị.V&iacute; dụ, khi m&agrave;n h&igrave;nh đang ở trạng th&aacute;i tắt, bạn c&oacute; thể chạm nhẹ 2 lần l&ecirc;n m&agrave;n h&igrave;nh để mở m&aacute;y, vẽ một h&igrave;nh tr&ograve;n để mở camera, gạch một đường ch&eacute;o để mở ứng dụng nghe nhạc,&hellip;</p>\\r\\n\\r\\n<h2><strong>Hai sim tiện dụng</strong></h2>\\r\\n\\r\\n<p>hỗ trợ người d&ugrave;ng hai khe cắm SIM, cho ph&eacute;p bạn c&oacute; thể sử dụng hai sim của hai nh&agrave; mạng kh&aacute;c nhau để thuận tiện hơn trong li&ecirc;n lạc cũng như dễ d&agrave;ng quản l&yacute; c&ocirc;ng việc v&agrave; cuộc sống ri&ecirc;ng tư hơn. Nếu bạn l&agrave; sinh vi&ecirc;n th&igrave; t&iacute;nh năng n&agrave;y cũng gi&uacute;p bạn tiết kiệm tối đa tiền cước di động m&agrave; kh&ocirc;ng cần phải tốn tiền để mua th&ecirc;m một chiếc điện thoại kh&aacute;c.</p>\\r\\n</div>\\r\\n', '03-58-14-30-04-2016-oppo-joy-3-2.jpg', '03-58-20-30-04-2016-oppo-joy-3-a11w-den2w6f9-mo.jpg', 'a:2:{i:0;s:36:\"03-58-14-30-04-2016-oppo-joy-3-2.jpg\";i:1;s:51:\"03-58-20-30-04-2016-oppo-joy-3-a11w-den2w6f9-mo.jpg\";}', '0', null, null, '44', 'Điện thoại', '0', '0', '1', '1', '16', 'Quang Huy Mobile', '1', '1', null, '0', '22', null, null, '1462006694', null, null, '1476067338');
INSERT INTO `web_product` VALUES ('227', null, 'Điện thoại di động Oppo Mirror 3 - R3001', '0', '0', '0', '0', '2', 'Bảo hành chính hãng, phụ kiện đầy đủ', '1', '<p>Loại m&agrave;n h&igrave;nh 4.7 inch, IPS LCD</p>\\r\\n\\r\\n<p>Tốc độ CPU Quad-core 1.2 GHz</p>\\r\\n\\r\\n<p>RAM 1 GB, Bộ nhớ trong 8 GB</p>\\r\\n\\r\\n<p>Camera sau:8.0 MP, Camera trước 5.0 MP</p>\\r\\n\\r\\n<p>Dung lượng pin 2000 mAh</p>\\r\\n', '<div class=\"std\">\\r\\n<h2><span style=\"font-size:medium\"><strong>Điện thoại di động Oppo Mirror 3</strong></span></h2>\\r\\n\\r\\n<p><span style=\"font-size:small\">Thiết kế nhỏ gọn thanh tho&aacute;t, vi xử l&yacute; l&otilde;i tứ Qualcomm 64bit c&ugrave;ng nhiều t&iacute;nh năng độc đ&aacute;o cho camera v&agrave; c&aacute;c trải nghiệm phong ph&uacute; từ ColorOS 2.0, Oppo Mirror 3 ch&iacute;nh l&agrave; sự lựa chọn đ&aacute;ng gi&aacute; cho những bạn trẻ năng động.</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">Thiết kế v&acirc;n chấm bi độc đ&aacute;o, s&agrave;nh điệu</span></strong></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\"><img alt=\"\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-di-dong-oppo-mirror-3-1.jpg\" /></span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Oppo Mirror 3 tạo ấn tượng bởi một thiết kế thời trang v&agrave; s&agrave;nh điệu. Mặt lưng được trang tr&iacute; bằng c&aacute;c họa tiết chấm bi c&ugrave;ng c&aacute;c điểm dập đều đặn với k&iacute;ch thước ch&iacute;nh x&aacute;c 0.5mm. Sản phẩm đ&atilde; phải trải qua 10 lần in, tạo h&igrave;nh v&agrave; phủ UV bảo vệ chống trầy để tạo th&agrave;nh mặt lưng với hiệu ứng lập thể 3D mang phong c&aacute;ch kho&aacute;ng đạt v&agrave; ẩn chứa cả sự tinh tế. B&ecirc;n cạnh đ&oacute;, m&aacute;y cũng được thiết kế kh&aacute; nhỏ gọn với m&agrave;n h&igrave;nh 4.7 inch v&agrave; trọng lượng 126g cho cảm gi&aacute;c thoải m&aacute;i khi cầm nắm v&agrave; sử dụng bằng một tay.</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">Bộ đ&ocirc;i camera chất lượng, nhiều t&iacute;nh năng xuất sắc</span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Camera tr&ecirc;n c&aacute;c sản phẩm của Oppo lu&ocirc;n được đ&aacute;nh gi&aacute; cao v&agrave; Mirror 3 cũng kh&ocirc;ng phải l&agrave; ngoại lệ. Với chiếc điện thoại n&agrave;y, c&ocirc;ng nghệ chụp ảnh tr&ecirc;n smartphone đ&atilde; được n&acirc;ng l&ecirc;n một tầm cao mới. Camera ch&iacute;nh c&oacute; độ ph&acirc;n giải 8MP sử dụng chip cảm biến IMX179 cao cấp của Sony, k&iacute;ch thước điểm ảnh l&ecirc;n đến 1.4 micromet, kết hợp cũng khẩu độ f/2.0 gi&uacute;p thu s&aacute;ng tốt hơn v&agrave; khả năng chụp đ&ecirc;m mạnh mẽ hơn. Đồng thời, hệ thống thấu k&iacute;nh 5P v&agrave; bộ lọc thủy tinh xanh cũng gi&uacute;p chống tia hồng ngoại hiệu quả v&agrave; ngăn c&aacute;c hiện tượng như mắt đỏ, ảnh mờ,&hellip; để mang đến cho bạn những bức ảnh ưng &yacute;, ch&acirc;n thực v&agrave; tự nhi&ecirc;n nhất trong mọi điều kiện &aacute;nh s&aacute;ng kể cả ban đ&ecirc;m hay những nơi c&oacute; điều kiện khắc nghiệt.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:small\"><img alt=\"\" height=\"375\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-di-dong-oppo-mirror-3-4.jpg\" width=\"500\" /></span></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Ngo&agrave;i ra, c&ocirc;ng nghệ Pure Image 2.0+ ti&ecirc;n tiến độc quyền từ Oppo c&ograve;n cho bạn nhiều điều bất ngờ hơn thế nữa. T&iacute;nh năng chụp ảnh nhanh gi&uacute;p bạn c&oacute; thể chớp được mọi khoảnh khắc chỉ trong nh&aacute;y mắt d&ugrave; điện thoại của bạn đang ở chế độ kh&oacute;a. T&iacute;nh năng chụp phơi s&aacute;ng gi&uacute;p bạn c&oacute; những bức ảnh độc đ&aacute;o, đầy nghệ thuật. B&ecirc;n cạnh đ&oacute; chế độ chụp ảnh động cho m&aacute;y khả năng chụp đến 20 bức ảnh chỉ trong 3 gi&acirc;y, bạn sẽ c&oacute; bộ ảnh đầy sống động m&agrave; kh&ocirc;ng cần ti&ecirc;u tốn qu&aacute; nhiều bộ nhớ như việc lưu trữ video.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:small\"><img alt=\"\" height=\"500\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-di-dong-oppo-mirror-3-2.jpg\" width=\"413\" /></span></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Kh&ocirc;ng chỉ c&oacute; camera ch&iacute;nh m&agrave; camera trước 5MP của Oppo Mirror 3 cũng được thể hiện rất xuất sắc với khả năng tự nhận diện t&acirc;m trạng của người d&ugrave;ng. Chế độ chụp khu&ocirc;n mặt đẹp Beauty Plus 3.0 với 6 c&aacute;ch l&agrave;m đẹp kh&aacute;c nhau (tự nhi&ecirc;n, kỉ niệm, cầu vồng,&hellip;) cũng sẽ gi&uacute;p bạn c&oacute; những tấm h&igrave;nh đẹp mắt, đầy ấn tượng. </span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">M&agrave;n h&igrave;nh 4.7 inch độ n&eacute;t cao</span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Độ ph&acirc;n giải chuẩn HD 1280x720 pixels tr&ecirc;n m&agrave;n h&igrave;nh k&iacute;ch thước 4.7 inch của Oppo Mirror v&agrave; mật độ điểm ảnh 312ppi l&agrave; qu&aacute; đủ để bạn c&oacute; thể thưởng thức những h&igrave;nh ảnh sắc n&eacute;t, trong trẻo v&agrave; ch&acirc;n thực d&ugrave; l&agrave; lướt web, đọc b&aacute;o, xem phim phim hay chơi game. C&ocirc;ng nghệ m&agrave;n h&igrave;nh IPS LCD gi&uacute;p cải thiện độ s&aacute;ng cho g&oacute;c nh&igrave;n rộng hơn v&agrave; tạo cảm gi&aacute;c thoải m&aacute;i, dịu nhẹ khi bạn thao t&aacute;c l&acirc;u với m&agrave;n h&igrave;nh.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:small\"><img alt=\"\" height=\"484\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-di-dong-oppo-mirror-3-3.jpg\" width=\"900\" /></span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">Vi xử l&yacute; l&otilde;i tứ Snapdragon 410 mạnh mẽ</span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Điện thoại Oppo Mirror 3 mang trong m&igrave;nh bộ vi xử l&yacute; l&otilde;i tứ Qualcomm MSM8916 Snapdragon 410 xung nhịp 1.2GHz kiến tr&uacute;c 64-bit, bộ nhớ RAM 1GB mang đến cho bạn những trải nghiệm mượt m&agrave; v&agrave; nhanh ch&oacute;ng ngay cả khi bạn đang chạy nhiều ứng dụng c&ugrave;ng l&uacute;c. Nếu bạn l&agrave; người th&iacute;ch chơi game tr&ecirc;n điện thoại di động th&igrave; Mirror 3 cũng sẽ l&agrave;m bạn h&agrave;i l&ograve;ng khi n&oacute; c&oacute; thể &ldquo;chiến&rdquo; được hết c&aacute;c game 3D nặng như Asphalt 8, Minion Rush,&hellip; Ngo&agrave;i ra, m&aacute;y c&ograve;n c&oacute; bộ nhớ trong 8GB v&agrave; hỗ trợ khe cắm thẻ nhớ ngo&agrave;i l&ecirc;n đến 128GB, nhờ đ&oacute; bạn sẽ c&oacute; một kh&ocirc;ng gian lưu trữ khổng lồ trong một thiết bị nhỏ gọn.</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">Thay thế mọi thiết bị điều khiển từ xa trong nh&agrave; bạn</span></strong></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\"><img alt=\"\" height=\"444\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-di-dong-oppo-mirror-3-5.jpg\" width=\"500\" /></span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">D&ugrave; trong nh&agrave; bạn c&oacute; h&agrave;ng trăm thiết bị kh&aacute;c nhau th&igrave; bạn chỉ cần ngồi một chỗ l&agrave; đ&atilde; c&oacute; thể điều khiển ch&uacute;ng một c&aacute;ch dễ d&agrave;ng nhờ t&iacute;nh năng điều khiển từ xa tr&ecirc;n Oppo Mirror 3. Đồng thời, t&iacute;nh năng độc đ&aacute;o n&agrave;y c&ograve;n c&oacute; khả năng định vị chương tr&igrave;nh. Theo đ&oacute;, sau khi kết nối với TV Box, người d&ugrave;ng c&oacute; thể l&ecirc;n mạng xem lịch chiếu, theo d&otilde;i tiết mục ưa th&iacute;ch một c&aacute;ch dễ d&agrave;ng v&agrave; thuận tiện.</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">C&ocirc;ng nghệ sạc nhanh, si&ecirc;u tiết kiệm pin</span></strong></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\"><img alt=\"\" height=\"375\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-di-dong-oppo-mirror-3-6.jpg\" width=\"500\" /></span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Sử dụng bộ sạc 5V &ndash; 2A, pin của Oppo Mirror 3 c&oacute; thể được sạc đầy chỉ trong v&ograve;ng 90 ph&uacute;t, nhanh hơn 60% so với c&aacute;c sản phẩm th&ocirc;ng thường. Đồng thời, sản phẩm cũng hỗ trợ c&ocirc;ng nghệ si&ecirc;u tiết kiệm pin. T&iacute;nh năng n&agrave;y sẽ tự động được k&iacute;ch hoạt khi dung lượng pin c&ograve;n dưới 10%. Ngo&agrave;i ra người d&ugrave;ng cũng c&oacute; thể tự k&iacute;ch hoạt chế độ n&agrave;y nếu muốn thời lượng sử dụng pin được l&acirc;u hơn. </span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">T&ugrave;y biến th&ocirc;ng minh Color OS 2.0</span></strong></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\"><img alt=\"\" height=\"440\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-di-dong-oppo-mirror-3-7.jpg\" width=\"550\" /></span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">T&iacute;nh năng kinh điển tr&ecirc;n Color OS 2.0 tiếp tục được Oppo duy tr&igrave; v&agrave; ph&aacute;t triển tr&ecirc;n Mirror 3 tạo sự kh&aacute;c biệt ho&agrave;n to&agrave;n so với c&aacute;c m&aacute;y Android hiện đang c&oacute; mặt tr&ecirc;n thị trường. V&iacute; dụ, khi m&agrave;n h&igrave;nh đang tắt, bạn chỉ cần vẽ chữ O để mở camera hay nhấp đ&uacute;p l&ecirc;n m&agrave;n h&igrave;nh để mở m&aacute;y hoặc vẽ chữ V để bật Flash. Bạn cũng c&oacute; thể thiết lập sẵn những h&igrave;nh vẽ đơn giản với 11 cử chỉ m&agrave;n h&igrave;nh cho c&aacute;c ứng dụng thường d&ugrave;ng nhất để v&agrave;o bất cứ l&uacute;c n&agrave;o.</span><br />\\r\\n<span style=\"font-size:small\">Kh&ocirc;ng chỉ vậy, giao diện nghe nhạc v&agrave; c&aacute;c icon cũng kho&aacute;c m&agrave;u &aacute;o mới tinh tế v&agrave; sang trọng, giống như c&aacute;c d&ograve;ng smartphone cao cấp như N3 hay R5.</span></p>\\r\\n</div>\\r\\n', '04-00-38-30-04-2016-dien-thoai-di-dong-oppo-mirror-3.jpg', '04-00-38-30-04-2016-dien-thoai-di-dong-oppo-mirror-3.jpg', 'a:1:{i:0;s:56:\"04-00-38-30-04-2016-dien-thoai-di-dong-oppo-mirror-3.jpg\";}', '0', null, null, '44', 'Điện thoại', '0', '0', '1', '1', '16', 'Quang Huy Mobile', '1', '1', null, '0', '22', null, null, '1462006838', null, null, '1476067338');
INSERT INTO `web_product` VALUES ('228', null, 'Điện thoại di động OPPO R5 - R8106S', '0', '0', '0', '0', '2', 'Bảo hành chính hãng, phụ kiện đầy đủ', '1', '<p>M&agrave;n h&igrave;nh: AMOLED 5.2 inch,</p>\\r\\n\\r\\n<p>Hệ điều h&agrave;nh : Android (4.4.2) ColorOS 2.0&nbsp;</p>\\r\\n\\r\\n<p>UICamera: Camera trước 5 megapixels, camera sau 13MP&nbsp;</p>\\r\\n\\r\\n<p>Chip xử l&yacute; : Qualcomm Snapdragon , 8-core, 1500 MHz</p>\\r\\n\\r\\n<p>Ram: 2048 MB, Rom: 16 GB</p>\\r\\n', '<div class=\"std\">\\r\\n<h2><strong><span style=\"font-size:medium\">Điện thoại di động &nbsp;OPPO R5</span></strong></h2>\\r\\n\\r\\n<p><strong><span style=\"font-size:medium\"><img alt=\"\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-oppo-r5.jpg\" /></span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Oppo R5 được đ&aacute;nh gi&aacute; l&agrave; chiếc điện thoại mỏng nhất tr&ecirc;n thế giới t&iacute;nh tới thời điểm n&agrave;y, với độ d&agrave;y th&acirc;n m&aacute;y chỉ đạt 4.8 mm. Đ&acirc;y cũng l&agrave; thiết bị di động đầu ti&ecirc;n của Oppo được trang bị chip xử l&yacute; 64 bit.</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">Oppo R5 sở hữu thiết kế tuyệt đẹp, th&acirc;n m&aacute;y cực mỏng.</span></strong></p>\\r\\n\\r\\n<p><strong><img alt=\"\" height=\"212\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-oppo-r5-d.jpg\" width=\"843\" /></strong></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\"><img alt=\"\" height=\"459\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-oppo-r5-e.jpg\" width=\"791\" /></span></strong></p>\\r\\n\\r\\n<p>&nbsp;<span style=\"font-size:small\">R5 c&oacute; k&iacute;ch thước 148,9 x 74.5mm v&agrave; trọng lượng 155g với vỏ được l&agrave;m ho&agrave;n to&agrave;n bằng hợp kim nh&ocirc;m, chỉ d&agrave;y c&oacute; 4.85mm. Phần khung của chiếc điện thoại qu&aacute; mỏng n&ecirc;n được chế t&aacute;c ho&agrave;n to&agrave;n thủ c&ocirc;ng, từ c&aacute;c kh&acirc;u tạo khu&ocirc;n, gia c&ocirc;ng cho đến đ&aacute;nh b&oacute;ng.&nbsp;</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">Vỏ m&aacute;y được l&agrave;m ho&agrave;n to&agrave;n bằng hợp kim nh&ocirc;m.</span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Khung m&aacute;y l&agrave;m bằng hợp kim nh&ocirc;m nguy&ecirc;n khối cũng gi&uacute;p sản phẩm bền hơn v&agrave; cứng c&aacute;p hơn so với c&aacute;c đối thủ c&ugrave;ng tầm gi&aacute; hiện nay. Mặt sau của m&aacute;y c&oacute; th&ecirc;m c&aacute;c dải nhựa đ&oacute;ng vai tr&ograve; cải thiện chất lượng bắt s&oacute;ng của c&aacute;c ăng ten thu s&oacute;ng WiFi, 3G, Bluetooth v&agrave; FM.</span></p>\\r\\n\\r\\n<p><img alt=\"\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-oppo-r5-i.jpg\" /><br />\\r\\n<span style=\"font-size:small\">Cạnh tr&aacute;i m&aacute;y l&agrave; nơi bố tr&iacute; khe gắn sim.</span><br />\\r\\n<span style=\"font-size:small\">Một điểm lạ tr&ecirc;n chiếc điện thoại R5 ch&iacute;nh l&agrave; jack cắm tai nghe 3.5 mm. Đ&acirc;y l&agrave; jack cắm chuẩn tr&ecirc;n mọi smartphone hiện nay, thế nhưng chiếc điện thoại mới của Oppo lại kh&ocirc;ng c&oacute; cổng cắm n&agrave;y. Thay v&agrave;o đ&oacute;, R5 được cung cấp tai nghe 3.5mm ti&ecirc;u chuẩn c&ugrave;ng với d&acirc;y c&aacute;p chuyển đổi cổng USB sang cổng &acirc;m thanh 3.5mm để sử dụng tai nghe.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">B&ecirc;n cạnh phải m&aacute;y l&agrave; nơi bố tr&iacute; 2 n&uacute;t bấm cứng chuy&ecirc;n dụng bao gồm n&uacute;t điều chỉnh &acirc;m lượng v&agrave; n&uacute;t nguồn. B&ecirc;n dưới ch&acirc;n m&aacute;y ch&iacute;nh l&agrave; nơi bố tr&iacute; jack cắm sạc. X&eacute;t về tổng thể th&igrave; th&acirc;n m&aacute;y của R5 kh&aacute; vu&ocirc;ng vức tuy nhi&ecirc;n do m&agrave;n h&igrave;nh m&aacute;y chỉ chiếm 5.2 inch n&ecirc;n bạn c&oacute; thể thoải m&aacute;i sử dụng thiết bị m&agrave; kh&ocirc;ng hề bị cấn tay.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:small\"><img alt=\"\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-oppo-r5-h.jpg\" /></span></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Dưới ch&acirc;n m&aacute;y l&agrave; nơi bố tr&iacute; jack cắm sạc v&agrave; mic lọc &acirc;m.</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">Camera hỗ trợ h&agrave;ng loạt t&iacute;nh năng chụp ảnh h&agrave;ng đầu hiện nay.</span></strong></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\"><strong><img alt=\"\" height=\"517\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-oppo-r5-f.jpg\" width=\"638\" /></strong></span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Mặt sau m&aacute;y ch&iacute;nh l&agrave; nơi bố tr&iacute; camera ch&iacute;nh c&ugrave;ng đ&egrave;n Flash LED. Chiếc camera n&agrave;y vẫn bị lồi ra so với phần th&acirc;n m&aacute;y, khiến chiếc smartphone bị d&agrave;y l&ecirc;n 0,8mm. Camera sau c&oacute; độ ph&acirc;n giải 13 megapixel, sử dụng cảm biến Sony IMX214 với khẩu độ f/2.0.</span></p>\\r\\n\\r\\n<p><strong><img alt=\"\" height=\"510\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-oppo-r5-b.jpg\" width=\"900\" /></strong><br />\\r\\n<br />\\r\\n<span style=\"font-size:small\">Camera ch&iacute;nh tr&ecirc;n m&aacute;y c&oacute; khả năng lấy n&eacute;t nhanh, hỗ trợ lựa chọn chụp ảnh độ ph&acirc;n giải cao 50MP, chế độ lấy n&eacute;t sau v&agrave; chế độ chụp phơi s&aacute;ng để cải thiện chất lượng ảnh ở m&ocirc;i trường thiếu s&aacute;ng. Trong khi đ&oacute; camera trước c&oacute; độ ph&acirc;n giải 5 megapixel cũng hứa hẹn cho ra c&aacute;c bức ảnh tự sướng đẹp.</span><br />\\r\\n<span style=\"font-size:small\">Camera hỗ trợ chụp ảnh to&agrave;n cảnh.</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">M&agrave;n h&igrave;nh sắc n&eacute;t, chip xử l&yacute; 64 bit, giao diện mới.</span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">B&ecirc;n cạnh thiết kế bắt mắt, R5 cũng sở hữu phần cứng đ&aacute;ng nể với m&agrave;n h&igrave;nh AMOLED 5.2 inch với độ ph&acirc;n giải 1080p v&agrave; mật độ điểm ảnh đạt 423ppi. Oppo cũng cho biết R5 được trang bị c&ocirc;ng nghệ hiển thị ti&ecirc;n tiến nhất, gi&uacute;p tăng cường độ s&aacute;ng, m&agrave;u sắc ch&acirc;n thực v&agrave; c&oacute; độ tương phản cao nhất trong tất cả c&aacute;c mẫu smartphone hiện nay.</span><br />\\r\\n<span style=\"font-size:small\">M&agrave;n h&igrave;nh sắc n&eacute;t, font chữ to r&otilde; r&agrave;ng, dễ đọc.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:small\"><strong><img alt=\"\" height=\"715\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-oppo-r5-a.jpg\" width=\"900\" /></strong></span></p>\\r\\n\\r\\n<p><span style=\"font-size:small\"><strong>M&aacute;y truy cập internet nhanh, hỗ trợ chạy đa nhiệm tốt.</strong></span></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Ngo&agrave;i việc được mệnh danh l&agrave; chiếc smartphone mỏng nhất thế giới, m&aacute;y cũng l&agrave; thiết bị cầm tay đầu ti&ecirc;n của nhả sản xuất Trung Quốc chạy vi xử l&yacute; 64-bit. Sức mạnh b&ecirc;n trong của R5 l&agrave; bộ vi xử l&yacute; Snapdragon 615, với chip l&otilde;i t&aacute;m Cortex-A53 tốc độ 1,5GHz, bộ nhớ RAM 2GB v&agrave; chip đồ họa 405 Adreno.</span>&nbsp;</p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Với trang bị d&ograve;ng chip n&agrave;y sẽ gi&uacute;p cho m&aacute;y vận h&agrave;nh tốt với mọi t&aacute;c vụ ngay cả với những t&aacute;c vụ nặng đ&ograve;i hỏi c&oacute; khả năng đồ họa cao th&igrave; m&aacute;y vẫn đ&aacute;p ứng tốt, kh&ocirc;ng c&oacute; độ trễ xảy ra. Do đ&oacute;, đ&acirc;y cũng sẽ l&agrave; một lựa chọn l&yacute; tưởng cho những bạn th&iacute;ch trải nghiệm game, xem phim, lướt web tr&ecirc;n một m&agrave;n h&igrave;nh lớn c&ugrave;ng chip xử l&yacute; mạnh.</span></p>\\r\\n\\r\\n<p>&nbsp;<span style=\"font-size:small\"><strong>M&aacute;y chạy tr&ecirc;n nền tảng hệ điều h&agrave;nh Android 4.4.4 Kitkat với giao diện ColorOS 2.0 UI.</strong></span></p>\\r\\n\\r\\n<p>&nbsp;<strong><img alt=\"\" height=\"497\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-oppo-r5-g.jpg\" width=\"790\" /></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">M&aacute;y chạy tr&ecirc;n nền tảng hệ điều h&agrave;nh Android 4.4.4 Kitkat với giao diện ColorOS 2.0 UI, hứa hẹn sẽ cung cấp th&ecirc;m nhiều t&iacute;nh năng mới m&agrave; chỉ c&oacute; thiết bị của Oppo mới c&oacute; được</span></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\">Sạc si&ecirc;u tốc VOOC</span></strong></p>\\r\\n\\r\\n<p><strong><span style=\"font-size:small\"><strong><img alt=\"\" height=\"452\" src=\"http://hc.com.vn/media/wysiwyg/Dien-thoai/dien-thoai-oppo-r5-c.jpg\" width=\"900\" /></strong></span></strong></p>\\r\\n\\r\\n<p><span style=\"font-size:small\">Mặc d&ugrave; rất mỏng, nhưng chiếc điện thoại n&agrave;y vẫn được trang bị pin với dung lượng 2000mAh. Đ&acirc;y kh&ocirc;ng phải l&agrave; mức dung lượng cao thế nhưng theo như Oppo cho biết, với mức dung lượng n&agrave;y, R5 vẫn c&oacute; thể đ&aacute;p ứng thời gian sử dụng cao cho người d&ugrave;ng. Đặc biệt, chiếc điện thoại n&agrave;y cũng đ&atilde; được nh&agrave; sản xuất trang bị cho c&ocirc;ng nghệ R5 sạc nhanh với tốc độ đầy 75% pin đầu ti&ecirc;n trong 30 ph&uacute;t.</span></p>\\r\\n</div>\\r\\n', '04-08-41-30-04-2016-dien-thoai-oppo4.jpg', '04-08-52-30-04-2016-dien-thoai-oppo6.jpg', 'a:3:{i:0;s:40:\"04-08-41-30-04-2016-dien-thoai-oppo4.jpg\";i:1;s:40:\"04-08-51-30-04-2016-dien-thoai-oppo1.jpg\";i:2;s:40:\"04-08-52-30-04-2016-dien-thoai-oppo6.jpg\";}', '0', null, null, '44', 'Điện thoại', '0', '0', '1', '1', '16', 'Quang Huy Mobile', '1', '1', null, '0', '22', null, null, '1462007321', null, null, '1476067338');
