INSERT INTO `web_product` VALUES ('582', '', 'Colosol điều trị viêm ruột, viêm đại tràng', '270000', '275000', '0', '0', '1', '', '1', '<p><strong>Colosol</strong><span style=\\\"color:rgb(84, 84, 84)\\\">&nbsp;gi&uacute;p tăng cường sức khỏe đường ti&ecirc;u h&oacute;a,hỗ trợ điều trị vi&ecirc;m ruột, vi&ecirc;m đại tr&agrave;ng mạn t&iacute;nh, giảm loạn khuẩn đường ruột, rối loạn ti&ecirc;u h&oacute;a. Gi&uacute;p hệ&nbsp;ti&ecirc;u h&oacute;a phục hồi nhanh hơn.</span></p>\\r\\n', '<p><strong>Colosol</strong> với th&agrave;nh phần từ thảo dược thi&ecirc;n nhi&ecirc;n c&oacute; c&ocirc;ng dụng gi&uacute;p:<br />\\r\\n+ Hỗ trợ điều trị v&agrave; ph&ograve;ng ngừa bệnh vi&ecirc;m đại tr&agrave;ng, cải thiện c&aacute;c triệu chứng của bệnh vi&ecirc;m đại tr&agrave;ng v&agrave; c&aacute;c biến chứng của bệnh vi&ecirc;m đại tr&agrave;ng.<br />\\r\\n+ Hỗ trợ điều trị v&agrave; ph&ograve;ng ngừa bệnh về đường ruột như ti&ecirc;u chảy, t&aacute;o b&oacute;n, đầy hơi, chướng bụng. Đồng thời gi&uacute;p bảo vệ v&agrave; tăng cường sức đề kh&aacute;ng v&agrave; hệ miễn dịch cho cơ thể.<br />\\r\\n+ Gi&uacute;p giảm hiện tượng loạn khuẩn đường ruột, rối loạn ti&ecirc;u h&oacute;a, ăn uống k&eacute;m do ti&ecirc;u chảy k&eacute;o d&agrave;i.</p>\\r\\n\\r\\n<p style=\"text-align:center\"><img src=\"http://shopcuatui.com.vn/uploads/product/582/05-14-31-02-06-2016-colosol.jpg\" /><br />\\r\\n&nbsp;</p>\\r\\n\\r\\n<p><strong>TH&Agrave;NH PHẦN trong colosol:</strong></p>\\r\\n\\r\\n<p><strong>+ Lactobacillus rhamnosus lysate</strong> l&agrave; một chủng vi khuẩn kh&aacute; tốt cho đường ti&ecirc;u h&oacute;a, n&oacute; bổ sung lợi khuẩn thiết lập thế c&acirc;n bằng vi sinh cho đường ruột.<br />\\r\\n+ <strong>Bạch phục linh</strong> hay c&ograve;n gọi l&agrave; phục linh c&oacute; vị ngon, tiết tả, lợi thủy, c&oacute; t&aacute;c dụng l&agrave;m ức chế sự ph&aacute;t triển c&aacute;c vi khuẩn, c&oacute; khả năng tăng cường miễn dịch.<br />\\r\\n+ <strong>Ho&agrave;i sơn</strong> đư