INSERT INTO `web_product` VALUES ('584', null, 'Gia vị rắc cơm các loại', '0', '0', '0', '0', '2', '', '1', '<p>Gia vị rắc cơm chay c&aacute;c loại - th&iacute;ch hợp cho những người&nbsp;ăn chay,&nbsp;ăn ki&ecirc;ng, tốt cho sức khoẻ.</p>\\r\\n', '<p><span style=\"font-family:arial,sans-serif; font-size:13.3333px\">Gia vị rắc cơm - Xu</span>ất&nbsp;xứ&nbsp;từ&nbsp;Nhật&nbsp;Bản<span style=\"font-family:arial,sans-serif; font-size:13.3333px\">, d&ugrave;ng&nbsp;ăn với cơm, ch&aacute;o, salad... th&iacute;ch hợp với mọi lứa tuổi, tầng lớp, những người muốn&nbsp;ăn chay,&nbsp;ăn ki&ecirc;ng. Với&nbsp;đầy&nbsp;đủ c&aacute;c th&agrave;nh phần dinh dưỡng c&oacute; sẵn trong gia vị, m&oacute;n&nbsp;ăn sẽ trở l&ecirc;n ngon miệng hơn, gi&uacute;p con người khoẻ khoắn hơn v&agrave; ph&ograve;ng tr&aacute;nh&nbsp;bệnh tật&nbsp;c&ugrave;ng&nbsp;c&aacute;c mối lo/nguy hại từ &quot;thực phẩm bẩn&quot;.</span></p>\\r\\n\\r\\n<p><span style=\"font-family:arial,sans-serif; font-size:13.3333px\">1. Gia v</span>ị rắc cơm vị thịt nướng:</p>\\r\\n\\r\\n<p style=\"text-align:justify\"><span style=\"font-family:arial,sans-serif; font-size:10pt\">Tr</span>ọng lượng: 55g</p>\\r\\n\\r\\n<p style=\"text-align:justify\"><span style=\"font-family:arial,sans-serif; font-size:10pt\">Th</span>&agrave;nh phần nguy&ecirc;n liệu:&nbsp;Đậu n&agrave;nh, dầu thực vật, tỏi, sốt đậu tương, hạt ti&ecirc;u, vừng, ớt.</p>\\r\\n\\r\\n<p style=\"text-align:justify\">Th&agrave;nh phần dinh dưỡng:&nbsp;Năng lượng: 272 Kcalo,&nbsp;Chất b&eacute;o: 16g</p>\\r\\n\\r\\n<p style=\"text-align:justify\">D&ugrave;ng&nbsp;để rắc cơm, ch&aacute;o, salad, sushi...</p>\\r\\n\\r\\n<p style=\"text-align:justify\">Gi&aacute; th&agrave;nh: 98.000 VNĐ/g&oacute;i</p>\\r\\n\\r\\n<p style=\"text-align:justify\">2. Gia vị rắc cơm vị mơ, vừng, rong biển</p>\\r\\n\\r\\n<p style=\"text-align:justify\">Trọng lượng: 31g</p>\\r\\n\\r\\n<p style=\"text-align:justify\">Th&agrave;nh phần nguy&ecirc;n liệu: mơ, vừng, rong biển, muối</p>\\r\