INSERT INTO `web_product` VALUES ('603', '', 'Sữa ong chúa Rebirth Platinum Royal Jelly', '550000', '619000', '500000', '0', '1', '', '1', '<p><span style=\\\"color:rgb(29, 33, 41)\\\">Sữa ong ch&uacute;a Rebirth Platinum Royal Jelly hỗ trợ tốt trong qu&aacute; tr&igrave;nh trị n&aacute;m da, đồi mồi v&agrave; t&agrave;n nhang, cho l&agrave;n da mịn m&agrave;ng, khỏe mạnh, ngăn ngừa rụng t&oacute;c, cho một m&aacute;i t&oacute;c su&ocirc;n mượt v&agrave; c&aacute;c m&oacute;ng tay, ch&acirc;n khỏe mạnh. Giảm triệu chứng xơ vữa động mạch v&agrave; cholesterol cao trong m&aacute;u. Tăng cường sinh lực, giảm stress.Nếu c&aacute;c bạn d&ugrave;ng k&eacute;o d&agrave;i 1-3 th&aacute;ng, ng&agrave;y uống hai vi&ecirc;n sữa ong ch&uacute;a Rebirth Platinum Royal Jelly, c&oacute; thể cải thiện t&iacute;ch cực t&igrave;nh trạng n&aacute;m da, trứng c&aacute; v&agrave; l&atilde;o h&oacute;a da, đồng thời cơ thể sẽ khỏe mạnh, y&ecirc;u đời hơn với một l&agrave;n da khỏe, mịn m&agrave;ng kh&ocirc;ng t&igrave; vết.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong><span style=\"color:rgb(0, 0, 0)\">&nbsp;60&nbsp;vi&ecirc;n nang/hộp</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">Thương hiệu:&nbsp;</span><strong>Rebirth</strong><br />\r\n<span style=\"color:rgb(0, 0, 0)\">Xuất xứ:</span><strong>&nbsp;&nbsp;&Uacute;c</strong></p>\r\n\r\n<p><strong>C&ocirc;ng dụng:</strong><br />\r\n<span style=\"color:rgb(0, 0, 0)\">- Tăng cường hệ miễn dịch,&nbsp;l&agrave;m chậm qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a, Trị n&aacute;m,&nbsp;l&agrave;m đẹp da.</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">- Hổ trợ tốt trong qu&aacute; tr&igrave;nh&nbsp;trị n&aacute;m da, đồi mồi v&agrave; t&agrave;n nhang, cho l&agrave;n da mịn m&agrave;ng, khỏe mạnh</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">- Ngăn ngừa rụng t&oacute;c, cho một m&aacute;i t&oacute;c su&ocirc;n mượt v&agrave; c&aacute;c m&oacute;ng tay, ch&acirc;n khỏe mạnh</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">- Giảm triệu chứng xơ vữa động mạch v&agrave; cholesterol cao trong m&aacute;u</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">- Tăng cường sinh lực, giảm stress</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">- Gi&uacute;p ngăn ngừa chứng vi&ecirc;m thấp khớp v&agrave; bệnh đa xơ cứng.</span></p>\r\n\r\n<p><strong>C&aacute;ch sử dụng:</strong><br />\r\n<span style=\"color:rgb(0, 0, 0)\">Uống 1 vi&ecirc;n/lần, c&oacute; thể d&ugrave;ng 1 đến 2 vi&ecirc;n mỗi ng&agrave;y. C&oacute; thể trộn trong nước, trộn với thức ăn hoặc chỉ đơn giản l&agrave; uống với nước.</span></p>\r\n\r\n<p><img alt=\"Sữa ong chúa Rebirth Platinum Royal Jelly\" src=\"http://shopcuatui.com.vn/uploads/thumbs/product/603/600x600/03-33-29-09-06-2016-lnt-sua-ong-chua500k.jpg\" /></p>\r\n\r\n<p><strong>M&ocirc; tả sản phẩm</strong><br />\r\n<span style=\"color:rgb(0, 0, 0)\">Sữa ong ch&uacute;a&nbsp;hay sữa ch&uacute;a, thạch ch&uacute;a, phong nhũ tinh (sản phẩm c&oacute; gi&aacute; trị cao nhất của tổ ong) l&agrave; loại thức ăn đặc biệt dưới dạng nhũ dịch do những con ong thợ tiết ra để d&agrave;nh ri&ecirc;ng cho việc nu&ocirc;i dưỡng ấu tr&ugrave;ng ong sau n&agrave;y trở th&agrave;nh con ong ch&uacute;a. Đ&oacute; l&agrave; một chất lỏng s&aacute;nh như mật, m&agrave;u trắng đục đến v&agrave;ng ng&agrave;, m&ugrave;i đặc biệt, vị ngọt, hơi chua, hơi kh&eacute; cổ, chứa 18% protein, 10-17% glucid, 5,5% lipid, khoảng 20 loại acid amin quan trọng, trong đ&oacute;, nhiều loại cơ thể con người kh&ocirc;ng tổng hợp được, nhất l&agrave; cystein, c&aacute;c acid hữu cơ, c&aacute;c vitamin A, B1, B2, B6, B12 , PP, c&aacute;c k&iacute;ch th&iacute;ch tố, muối kho&aacute;ng, một số enzym, hormon hướng sinh dục.&nbsp;</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">&nbsp;</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">Sữa ong ch&uacute;a&nbsp;được coi l&agrave; loại thuốc bổ cao cấp, c&oacute; t&aacute;c dụng k&iacute;ch th&iacute;ch phần giữa của n&atilde;o, tuyến y&ecirc;n dưới n&atilde;o v&agrave; tuyến thượng thận, tăng cường tuần ho&agrave;n huyết dịch v&agrave; điều h&ograve;a chức năng của c&aacute;c cơ quan trong cơ thể bị l&atilde;o h&oacute;a, được d&ugrave;ng cho người mới ốm dậy, người gi&agrave; yếu, phụ nữ sau khi&nbsp;sinh bị thiếu m&aacute;u, &iacute;t sữa. Ngo&agrave;i ra, sữa ong ch&uacute;a c&ograve;n được sử dụng l&agrave;m vị thuốc&nbsp;hổ trợ&nbsp;chữa đau dạ d&agrave;y, ruột, tăng huyết &aacute;p, huyết &aacute;p thấp, vi&ecirc;m mũi dị ứng v&agrave; c&oacute; khả năng k&igrave;m h&atilde;m một số vi khuẩn g&acirc;y bệnh, giảm hiện tượng stress ở người lao động qu&aacute; mức v&agrave; cải thiện hệ thống miễn dịch.&nbsp;</span></p>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0)\">Theo t&acirc;y y,&nbsp;sữa ong ch&uacute;a&nbsp;c&oacute; c&aacute;c t&aacute;c dụng l&agrave;m giảm triệu chứng mệt mỏi, mất ngủ; l&agrave;m giảm triệu chứng xơ vữa động mạch v&agrave; cao cholesterol trong m&aacute;u; l&agrave;m chậm qu&aacute; tr&igrave;nh l&atilde;o ho&aacute;; tăng cường hệ miễn dịch; gi&uacute;p ngăn ngừa chứng vi&ecirc;m thấp khớp v&agrave; bệnh đa xơ cứng.</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">&nbsp;</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">Một số nghi&ecirc;n cứu chỉ ra rằng&nbsp;sữa ong ch&uacute;a&nbsp;tương tự như hormon, c&oacute; khả năng điều h&ograve;a kinh nguyệt v&agrave; k&iacute;ch th&iacute;ch sinh dục trong một số trường hợp. Uống sữa ong ch&uacute;a thấy bệnh trạng thuy&ecirc;n giảm r&otilde; rệt ở người mắc bệnh liệt rung (Parkinson) v&agrave; bệnh nh&acirc;n bị nhiễm ph&oacute;ng xạ. C&ograve;n đối với người bị liệt dương, sữa ong ch&uacute;a l&agrave;m khả năng sinh dục dần dần được hồi phục.&nbsp;</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">&nbsp;</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">Sản phẩm n&agrave;y được sản xuất bằng ch&iacute;nh b&agrave;n tay của người &Uacute;c, chiết lọc từ những th&agrave;nh phần tốt&nbsp;nhất từ thi&ecirc;n nhi&ecirc;n tinh khiết v&agrave; bất tận của Australia.&nbsp;Đ&oacute;&nbsp;l&agrave; kh&aacute;c biệt lớn nhất&nbsp;so với rất nhiều&nbsp;sản phẩm tr&ecirc;n thị trường. Nếu b&aacute;c sĩ của bạn n&oacute;i rằng bạn c&oacute; bất cứ vấn đề g&igrave; khi sử dụng sản phẩm chất lượng n&agrave;y, C&ocirc;ng ty&nbsp;Lanopearl xin trang trải mọi chi ph&iacute; y tế li&ecirc;n quan v&igrave; c&ocirc;ng ty ch&uacute;ng t&ocirc;i chi một khoản tiền lớn cho gi&aacute; trị bảo hiểm cho kh&aacute;ch h&agrave;ng to&agrave;n cầu l&ecirc;n đến 5 triệu đ&ocirc; la &Uacute;c hằng năm.</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">&nbsp;</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">Nếu bạn thực sự quan t&acirc;m đến chất lượng, muốn cảm nhận chất lượng cao của</span>&nbsp;<span style=\"color:rgb(0, 0, 0)\">sữa ong ch&uacute;a&nbsp;thật từ Australia, ch&uacute;ng t&ocirc;i khuy&ecirc;n bạn đừng bỏ qua sản phẩm n&agrave;y.</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">&nbsp;</span><br />\r\n<strong>Chống chỉ định:</strong><br />\r\n<span style=\"color:rgb(0, 0, 0)\">Kh&ocirc;ng d&ugrave;ng cho người dưới 18 tuổi</span><br />\r\n<span style=\"color:rgb(0, 0, 0)\">Kh&ocirc;ng d&ugrave;ng cho những người bị hen suyễn v&agrave; bị dị ứng với phấn hoa</span></p>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<p>&nbsp;</p>\r\n', '03-33-29-09-06-2016-lnt-sua-ong-chua500k.jpg', '03-33-29-09-06-2016-lnt-sua-ong-chua500k.jpg', 'a:1:{i:0;s:44:\"03-33-29-09-06-2016-lnt-sua-ong-chua500k.jpg\";}', '0', null, null, '236', '---------- Thuốc - Sức khỏe', '0', '0', '1', '1', '55', 'Siêu thị Hàng Xách tay', '3', '1', '-1', '0', '22', null, null, '1465461209', null, null, '1488683984');
