INSERT INTO `web_product` VALUES ('616', '', 'Trà Đinh Lăng hỗ trợ điều trị tiền đình', '65000', '0', '0', '0', '1', '', '1', '<p style=\\\"margin-left:0in; margin-right:0in\\\">Đinh lăng tr&agrave; TRC&nbsp;Gi&uacute;p tăng cường tuần ho&agrave;n n&atilde;o, giảm t&igrave;nh trạng căng thẳng thần kinh, đau đầu, hoa mắt, ch&oacute;ng mặt, &ugrave; tai.&nbsp;Gi&uacute;p thanh nhiệt, lợi tiểu, lưu th&ocirc;ng huyết mạch.</p>\\r\\n', '<p><span style=\"font-size:16px\">Nhiều người biết đến đinh lăng như một loại rau thơm quen thuộc, l&agrave; một loại c&acirc;y cảnh th&ocirc;ng dụng, nhưng &iacute;t ai biết được đinh lăng lại c&oacute; những t&aacute;c dụng thần kỳ, người ta c&oacute; thể sử dụng to&agrave;n bộ c&aacute;c bộ phận của c&acirc;y: th&acirc;n, c&agrave;nh, l&aacute;, vỏ, bộ rễ để bồi bổ sức khỏe, điều trị bệnh.&nbsp;Loại c&acirc;y n&agrave;y c&ograve;n gi&uacute;p tăng khả năng th&iacute;ch nghi của cơ thể người đối với nhiệt độ n&oacute;ng, rối loạn tiền đ&igrave;nh, nhiễm k&yacute; sinh tr&ugrave;ng sốt r&eacute;t, trạng th&aacute;i stress. Đinh lăng c&oacute; t&aacute;c dụng g&acirc;y hoạt h&oacute;a nhẹ, đồng bộ ở c&aacute;c tế b&agrave;o thần kinh, tăng bi&ecirc;n độ điện thế vỏ n&atilde;o, tăng hoạt động của c&aacute;c tế b&agrave;o thần kinh, tăng độ nhớ.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\">Sản phẩm Đinh Lăng Tr&agrave; - TRC với th&agrave;nh phần ch&iacute;nh l&aacute; đinh lăng kết hợp với c&aacute;c vị thảo dược&nbsp;l&aacute; sen, l&aacute; vằng, cỏ ngọt để tạo ra một sản phẩm với c&ocirc;ng dụng tốt cho sức khỏe.</span></p>\\r\\n\\r\\n<p style=\"margin-left:0in; margin-right:0in\"><span style=\"font-size:16px\">&ndash; Gi&uacute;p tăng cường tuần ho&agrave;n n&atilde;o, giảm t&igrave;nh trạng căng thẳng thần kinh, đau đầu, hoa mắt, ch&oacute;ng mặt, &ugrave; tai.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\">&ndash; Gi&uacute;p thanh nhiệt, lợi tiểu, lưu th&ocirc;ng huyết mạch.</span></p>\\r\\n\\r\\n<p style=\"text-align:center\"><img src=\"http://shopcuatui.com.vn/uploads/product/616/10-36-48-16-06-2016-dinh-lang-tra-trc.png\" /></p>\\r\\n\\r\\n<p style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"font-family:times new roman,times,serif\"><strong>Đinh lăng tr&agrave; TRC điều trị tiền đ&igrave;nh hiệu quả</strong></span></span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\"><strong>Hướng dẫn sử dụng:</strong></span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\">Nh&uacute;ng t&uacute;i tr&agrave; v&agrave;o trong nước s&ocirc;i, chờ 3-5 ph&uacute;t.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\">N&ecirc;n pha, h&atilde;m trong ph&iacute;ch, ấm hoặc nấu ở nước c&oacute; nhiệt độ 100 độ C.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\">Lưu &yacute;: Sản phẩm n&agrave;y kh&ocirc;ng phải l&agrave; thuốc, kh&ocirc;ng c&oacute; t&aacute;c dụng thay thế thuốc chữa bệnh.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\"><strong>Đối tượng:&nbsp;</strong></span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\">&ndash; Người bị mệt mỏi, tuần ho&agrave;n n&atilde;o k&eacute;m.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\">&ndash; Người lao động tr&iacute; &oacute;c, thần kinh căng thẳng</span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\"><strong>Bảo quản:</strong></span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\">Bảo quản nơi kh&ocirc; m&aacute;t, nhiệt độ 20- 25 độ C, tr&aacute;nh &aacute;nh nắng trực tiếp.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\"><strong>Chất liệu bao b&igrave;:</strong></span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\">Sản phẩm được đ&oacute;ng trong g&oacute;i k&iacute;n, đảm bảo vệ sinh anh to&agrave;n thực phẩm.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\"><strong>Quy c&aacute;ch đ&oacute;ng g&oacute;i:</strong></span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\">Hộp 20 g&oacute;i x 2gr.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:16px\"><strong>Gi&aacute;