INSERT INTO `web_product` VALUES ('617', '', 'Hạt chia úc Black Bag Chia giá rẻ nhất', '0', '0', '0', '0', '2', '', '1', '<p><strong>Hạt Chia</strong>&nbsp;l&agrave; loại hạt dinh dưỡng c&oacute; chứa h&agrave;m lượng acid b&eacute;o cao v&agrave; lượng omega 3 vượt trội hơn rất nhiều loại thực phẩm trong đ&oacute; c&oacute; cả hải sản. Sử dụng hạt chia h&agrave;ng ng&agrave;y gi&uacute;p bổ sung:&nbsp;Omega 6,&nbsp;Chất xơ,&nbsp;Đạm,&nbsp;C&aacute;c loại vitamin,&nbsp;Kho&aacute;ng chất.</p>\\r\\n', '<p>Nh&igrave;n b&ecirc;n ngo&agrave;i,&nbsp;<strong>Hạt Chia</strong>&nbsp;tr&ocirc;ng giống với hạt &eacute; hay hạt m&egrave;, tuy nhi&ecirc;n h&agrave;m lượng dinh dưỡng m&agrave; hạt Chia mang lại lớn hơn rất nhiều. C&aacute;c nh&agrave; khoa học cũng từng c&oacute; nhiều nghi&ecirc;n cứu v&agrave; khẳng định rằng Hạt Chia tốt cho sức khỏe, tăng năng lượng cho người thường xuy&ecirc;n vận động v&agrave; cải thiện sức khỏe tim mạch.</p>\\r\\n\\r\\n<p>L&agrave; đất nước nổi tiếng với c&aacute;c sản phẩm hạt chia cao cấp,&nbsp;<strong>Hạt Chia &Uacute;c</strong>&nbsp;lu&ocirc;n l&agrave; sự lựa chọn của rất nhiều người trong đ&oacute; nổi bật l&agrave; Hạt Chia &Uacute;c Black Bag Chia 500g.</p>\\r\\n\\r\\n<p style=\"text-align:center\"><img src=\"http://shopcuatui.com.vn/uploads/product/617/06-13-51-17-06-2016-hat-chia-uc.jpg\" /></p>\\r\\n\\r\\n<p><strong>C&ocirc;ng dụng của Hạt Chia &Uacute;c Black Bag Chia (500g)</strong></p>\\r\\n\\r\\n<p>-&nbsp; Hạt Chia &Uacute;c được biết đến l&agrave; nguồn bổ sung năng lượng tuyệt vời cho cơ thể.</p>\\r\\n\\r\\n<p>- H&agrave;m lượng Omega cao gi&uacute;p ổn định sức khỏe tim mạch v&agrave; tr&iacute; n&atilde;o.</p>\\r\\n\\r\\n<p>- Bổ sung chất chống oxi h&oacute;a, l&agrave;m chậm qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a trong cơ thể.</p>\\r\\n\\r\\n<p>- Hạt Chia tốt cho người bị bệnh tiểu đường.</p>\\r\\n\\r\\n<p>- L&agrave;m giảm cholesterol trong m&aacute;u v&agrave; gi&uacute;p ổn định huyết &aacute;p.</p>\\r\\n\\r\\n<p>- Hạt Chia &Uacute;c c&ograve;n chứa h&agrave;m lượng lớn chất xơ tốt cho hệ ti&ecirc;u h&oacute;a, ổn định chức năng đường ruột, ngăn ngừa ung thư đường ruột.</p>\\r\\n\\r\\n<p>- Với những người muốn giảm c&acirc;n c&oacute; thể sử dụng hạt chia như 1 loại thực phẩm hỗ trợ, trong hạt chia chứa h&agrave;m lượng chất xơ cao, tạo cảm gi&aacute;c no nhanh ch&oacute;ng gi&uacute;p bạn ăn &iacute;t hơn trong bữa ăn h&agrave;ng ng&agrave;y.</p>\\r\\n\\r\\n<p>- Hạt Chia tuyệt đối an to&agrave;n, th&iacute;ch hợp với mọi người v&agrave; mọi lứa tuổi.</p>\\r\\n\\r\\n<p><strong>Gi&aacute; trị dinh dưỡng tuyệt vời của Hạt Chia:</strong></p>\\r\\n\\r\\n<p>- H&agrave;m lượng omega 3 nhiều hơn c&aacute; hồi 700%</p>\\r\\n\\r\\n<p>- H&agrave;m lượng canxi nhiều hơn sữa 500%</p>\\r\\n\\r\\n<p>- H&agrave;m lượng magi&ecirc; nhiều hơn b&ocirc;ng cải xanh 1400%</p>\\r\\n\\r\\n<p>- H&agrave;m lượng protein nhiều hơn đậu t&acirc;y 500%</p>\\r\\n\\r\\n<p>- H&agrave;m lượng selen nhiều hơn hạt lanh 300%</p>\\r\\n\\r\\n<p>- H&agrave;m lượng chất sắt nhiều hơn rau bina 200%</p>\\r\\n\\r\\n<p>- H&agrave;m lượng photpho nhiều hơn sữa 800%</p>\\r\\n\\r\\n<p>- Kh&ocirc;ng những thế hạt chia&nbsp;c&ograve;n rất gi&agrave;u chất xơ v&agrave; kali hơn rất nhiều c&aacute;c thực phẩm m&agrave; ch&uacute;ng ta vẫn nạp v&agrave;o cơ thể h&agrave;ng ng&agrave;y.</p>\\r\\n\\r\\n<p><strong>Hướng dẫn sử dụng Hạt Chia &Uacute;c Black Bag Chia (500g) hiệu quả nhất:</strong></p>\\r\\n\\r\\n<p>- Cho 1 th&igrave;a hạt chia ( khoảng 10g) v&agrave;o c&ugrave;ng với nước tr&aacute;i c&acirc;y, quậy đều để kh&ocirc;ng bị v&oacute;n cục. Sau khoảng 10 ph&uacute;t l&agrave; c&oacute; thể thưởng thức.</p>\\r\\n\\r\\n<p>- Hạt Chia dạng gel c&oacute; thể bảo quản trong 2 tuần bằng c&aacute;ch: sau 10 ph&uacute;t đ&aacute;nh đều l&ecirc;n th&ecirc;m 1 lần nữa rồi cho v&agrave;o hộp đ&oacute;ng k&iacute;n, cất v&agrave;o tủ lạnh. Bất cứ khi n&agrave;o muốn sử dụng, bạn c&oacute; thể trộn với đồ uống hoặc thực phẩm y&ecirc;u th&iacute;ch.</p>\\r\\n\\r\\n<p>- Một c&aacute;ch chế biến đơn giản nữa đ&oacute; ch&iacute;nh l&agrave; rắc hạt Chia trực tiếp l&ecirc;n c&aacute;c m&oacute;n ăn như salad, ngũ cốc, kem, b&aacute;nh ngọt, sữa chua, b&aacute;nh m&igrave;...</p>\\r\\n\\r\\n<p>- Hạt CHIA c&oacute; thể d&ugrave;ng chung với ch&aacute;o.</p>\\r\\n\\r\\n<p>- C&oacute; thể ăn trực tiếp nhưng nhớ uống nhiều nước.</p>\\r\\n\\r\\n<p><strong>Trọng lượng:</strong>&nbsp;500g</p>\\r\\n\\r\\n<p><strong>Bảo quản:</strong>&nbsp;Nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t.</p>\\r\\n\\r\\n<p><strong>Hạn sử dụng</strong>: In tr&ecirc;n bao b&igrave;.</p>\\r\\n\\r\\n<p><strong>Xuất xứ</strong>: &Uacute;c.</p>\\r\\n\\r\\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong>LI&Ecirc;N HỆ MUA H&Agrave;NG:</strong></span></span></p>\\r\\n\\r\\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong>Hệ thống CH EnMax - Số 5/189 Giảng V&otilde;, Đống Đa, H&agrave; Nội.</strong><br />\\r\\n<strong>Hotline: 0932 292 136.<br />\\r\\nEnmax giao h&agrave;ng tận nơi tr&ecirc;n to&agrave;n quốc. Nhận h&agrave;ng thanh to&aacute;n tại nh&agrave;.</strong></span></span></p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n', '06-13-51-17-06-2016-hat-chia-uc.jpg', '06-13-51-17-06-2016-hat-chia-uc.jpg', 'a:1:{i:0;s:35:\"06-13-51-17-06-2016-hat-chia-uc.jpg\";}', '0', null, null, '196', 'Thực phẩm chức năng', '0', '0', '1', '1', '53', 'EnMax ', '3', '1', '-1', '0', '22', null, null, '1466162031', null, null, '1477988017');
INSERT INTO `web_product` VALUES ('618', null, 'Kem chăm sóc giày GoldCare - GC 1004 ', '76000', '0', '0', '0', '1', '', '1', '<p>Sáp tự nhiên bổ sung dưỡng chất giúp nuôi dưỡng, bảo vệ và cung cấp độ đàn hồi cho da, giúp da giày mềm mại và bền màu. Thích hợp cho giày ống trong mùa lạnh và các loại giày có tính năng chống thấm nước.</p>\\r\\n', '<p><span style=\"font-size:12px\">- </span>Kem chăm sóc giày GoldCare - GC 1004 <span style=\"font-size:12px\"> là sản phẩm có xuất xứ từ Thổ Nhĩ Kỳ, được nhập khẩu và phân phối độc quyền bởi Công ty Cổ phần Gilos. </span></p>\\r\\n\\r\\n<p><span style=\"font-size:12px\">- </span>Kem chăm sóc giày với thành phần sáp tự nhiên giúp bổ sung chất dưỡng da, nuôi dưỡng, bảo vệ và tăng tính mềm mại, đàn hồi trên da.</p>\\r\\n\\r\\n<p>- Bảo vệ da không bị bong tróc, rạn nứt giúp sử dụng lâu hơn.</p>\\r\\n\\r\\n<p>- Có các màu: đen, nâu, tự nhiên.</p>\\r\\n\\r\\n<p><span style=\"font-size:12px\"><strong>Thông tin liên hệ:<br />\\r\\nCông ty Cổ Phần Gilos<br />\\r\\nĐịa chỉ: Tầng 4, Tòa nhà Thanh Niên, 345/134 Trần Hưng Đạo, P.Cầu Kho, Quận 1, TP.HCM<br />\\r\\nĐiện thoại: (08) 22 242 168 - 0908 340 057<br />\\r\\nEmail: trieubui@gilos.com.vn<br />\\r\\nWebsite: www.Gilos.com.vn</strong></span></p>\\r\\n\\r\\n<p><img alt=\"\" height=\"236\" src=\"data:image/jpeg;base64,\" width=\"236\" /></p>\\r\\n\\r\\n<p><img alt=\"\" height=\"227\" src=\"data:image/jpeg;base64,\" width=\"227\" /></p>\\r\\n\\r\\n<p><img alt=\"\" height=\"240\" src=\"data:image/jpeg;base64,\" width=\"240\" /></p>\\r\\n', '04-58-32-20-06-2016-gc1004-tn-1.jpg', '04-59-36-20-06-2016-gc1004nau-2.jpg', 'a:3:{i:0;s:35:\"04-58-32-20-06-2016-gc1004-tn-1.jpg\";i:1;s:35:\"04-58-48-20-06-2016-gc1004den-1.jpg\";i:2;s:35:\"04-59-36-20-06-2016-gc1004nau-2.jpg\";}', '0', null, null, '139', 'Giày dép, túi sách Nam', '0', '0', '1', '1', '32', 'Công ty CP Gilos', '1', '1', null, '0', '29', null, null, '1466416592', null, null, '1466417172');
