INSERT INTO `web_product` VALUES ('671', null, '[HEALTHY CARE VITAMIN E 500IU] - 200VIÊN', '610000', '680000', '580000', '0', '1', '', '1', '<p><span style=\"color:rgb(29, 33, 41)\">Vitamin E l&agrave; một chất chống oxy h&oacute;a dinh dưỡng quan trọng, gi&uacute;p bảo vệ tế b&agrave;o khỏi gốc tự do,duy tr&igrave; cholesterol, duy tr&igrave; sức khỏe tim mạch,hỗ trợ sức khỏe của mắt.</span></p>\\r\\n', '<p><span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">Trong thực phẩm, c&aacute;c nguồn phổ biến nhất chứa vitamin E l&agrave; c&aacute;c loại dầu thực vật như cọ dầu, hướng dương, ng&ocirc;, đậu tương, &ocirc; liu. C&aacute;c loại quả ki&ecirc;n, hạt hướng dương, quả nh&oacute;t gai (Hippophae spp.), dương đ&agrave;o (Actinidia spp.) v&agrave; mầm l&uacute;a m&igrave; cũng l&agrave; c&aacute;c nguồn cung cấp vitamin E. C&aacute;c nguồn kh&aacute;c c&oacute; hạt ngũ cốc, c&aacute;, bơ lạc, c&aacute;c loại rau&nbsp;l&aacute; xanh. Mặc d&ugrave; ban đầu vitamin E được chiết ra từ dầu mầm l&uacute;a m&igrave;, nhưng phần lớn c&aacute;c nguồn bổ sung vitamin E tự nhi&ecirc;n hiện nay lại t&aacute;ch ra từ dầu thực vật, th&ocirc;ng thường l&agrave; dầu đậu tương.</span><br />\\r\\n<img src=\"http://shopcuatui.com.vn/uploads/product/671/11-22-15-08-07-2016-lnt-healthy-caren580.jpg\" /><br />\\r\\n<span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\">H&agrave;m lượng vitamin E của một số nguồn như sau:<br />\\r\\nDầu mầm l&uacute;a m&igrave; (215,4 mg/100 g)<br />\\r\\nDầu hướng dương (55,8 mg/100 g)<br />\\r\\nQuả phỉ (26.0 mg/100 g)<br />\\r\\nDầu &oacute;c ch&oacute; (20,0 mg/100 g)<br />\\r\\nDầu lạc (17,2 mg/100 g)<br />\\r\\nDầu &ocirc; liu (12,0 mg/100 g)<br />\\r\\nLạc (9,0 mg/100 g)<br />\\r\\nC&aacute;m mịn (2,4 mg/100 g)<br />\\r\\nNg&ocirc; (2,0 mg/100 g)<br />\\r\\nMăng t&acirc;y (1,5 mg/100 g)<br />\\r\\nYến mạch (1,5 mg/100 g)<br />\\r\\nDẻ (1,2 mg/100 g)<br />\\r\\nDừa (1,0 