INSERT INTO `web_product` VALUES ('673', '', 'Viên uống bổ gan và giải độc - Swisse Liver Detox 120v', '560000', '0', '540000', '0', '1', '', '1', '<p><span style=\\\"color:rgb(29, 33, 41)\\\">Vi&ecirc;n uống bổ gan giải độc Swisse Ultiboost Liver Detox c&oacute; chứa c&aacute;c th&agrave;nh phần truyền thống, chất lượng cao được sử dụng để hỗ trợ gan v&agrave; trợ gi&uacute;p giải độc.</span><br />\r\n<span style=\\\"color:rgb(29, 33, 41)\\\">Gan l&agrave; cơ quan nội tạng lớn nhất trong cơ thể con người v&agrave; c&oacute; chức năng loại bỏ độc tố, hỗ trợ ti&ecirc;u h&oacute;a v&agrave; gi&uacute;p điều chỉnh qu&aacute; tr&igrave;nh trao đổi chất.</span></p>\r\n', '<p><span style=\"font-size:14px\"><span style=\"color:rgb(255, 140, 0)\"><strong>Th&ocirc;ng tin sản phẩm:</strong></span></span></p>\\r\\n\\r\\n<ul>\\r\\n	<li><span style=\"font-size:14px\">H&atilde;ng Sản xuất &nbsp; &nbsp;&nbsp; : Swisse Úc &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></li>\\r\\n	<li><span style=\"font-size:14px\">Qui c&aacute;ch đ&oacute;ng g&oacute;i: 120 vi&ecirc;n</span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li>\\r\\n</ul>\\r\\n\\r\\n<p><span style=\"font-size:14px\">Swisse Ultiboost gan Detox c&oacute; chứa th&agrave;nh phần truyền thống chất lượng cao được sử dụng để hỗ trợ chức năng gan v&agrave; trợ g&uacute;p giải độc.<br />\\r\\n<br />\\r\\nGan l&agrave; cơ quan nội tạng lớn nhất trong cơ thể con người v&agrave; c&oacute; tr&aacute;ch nhiệm loại bỏ độc tố, gi&uacute;p ti&ecirc;u h&oacute;a v&agrave; gi&uacute;p điều chỉnh sự trao đổi chất.<br />\\r\\n<br />\\r\\nSwisse Ultiboost Liver Detox chứa c&acirc;y kế &nbsp;sữa, l&agrave; loại c&acirc;y trong truyền thống được sử dụng như một loại thuốc bổ gan để gi&uacute;p hỗ trợ v&agrave; bảo vệ sức khỏe cho gan v&agrave; hỗ trợ t&aacute;i sinh của c&aacute;c tế b&agrave;o gan. &nbsp;Sản phẩm bao gồm cả Atis&ocirc; để gi&uacute;p l&agrave;m giảm chứng kh&oacute; ti&ecirc;u v&agrave; đầy hơi.<br />\\r\\n<br />\\r\\nC&ocirc;ng thức c&oacute; chứa bột nghệ, gi&uacute;p chống lại hư tổn tiềm năng do c&aacute;c tế b&agrave;o gốc tự do t&aacute;c động &nbsp;đến gan. Th&agrave;nh phần n&agrave;y được v&agrave; sử dụng như một chất chống vi&ecirc;m từ rất l&acirc;u trong lịch sử l&acirc;u d&agrave;i.</span></p>\\r\\n\\r\\n<p><span style=\"font-size:14px\">Sản phẩm nằm trong top những sản phẩm thực phẩm chức năng b&aacute;n chạy nhất tại &Uacute;c.</span></p>\\r\\n\\r\\n<p><img src=\"http://shopcuatui.com.vn/uploads/product/673/04-33-05-08-07-2016-lntvien-uong-bo-gan-va-giai-doc-swisse-liver-detox-120vn540b560.jpg\" /></p>\\r\\n\\r\\n<p><span style=\"color:rgb(255, 140, 0)\"><span style=\"font-size:14px\"><strong>Tính năng và c&ocirc;ng dụng:</strong></span></span></p>\\r\\n\\r\\n<ul>\\r\\n	<li><span style=\"font-size:14px\">&bull; Tăng cường chức năng gan</span></li>\\r\\n	<li><span style=\"font-size:14px\">&bull; Hỗ trợ đ&agrave;o thải c&aacute;c độc tố, thanh lọc cơ thể</span></li>\\r\\n	<li><span style=\"font-size:14px\">&bull; Chống oxy h&oacute;a, l&agrave;m chậm qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a của gan</span></li>\\r\\n	<li><span style=\"font-size:14px\">&bull; Hỗ trợ ti&ecirc;u h&oacute;a</span></li>\\r\\n	<li><span style=\"font-size:14px\">&bull; Hỗ trợ trong qu&aacute; tr&igrave;nh điều trị c&aacute;c bệnh c&aacute;c bệnh về gan</span></li>\\r\\n	<li>&nbsp;</li>\\r\\n</ul>\\r\\n\\r\\n<p><span style=\"color:rgb(255, 140, 0)\"><span style=\"font-size:14px\"><strong>Hướng dẫn sử dụng:</strong></span></span></p>\\r\\n\\r\\n<p><span style=\"font-size:14px\">Người lớn uống 2 vi&ecirc;n/ng&agrave;y, trong hoặc ngay sau bữa ăn, hoặc theo hướng dẫn của b&aacute;c sỹ.</span></p>\\r\\n\\r\\n<p><span style=\"color:rgb(255, 140, 0)\"><span style=\"font-size:14px\"><strong>Th&agrave;nh phần:</strong></span></span></p>\\r\\n\\r\\n<p><span style=\"font-size:14px\">Mỗi vi&ecirc;n chứa:</span></p>\\r\\n\\r\\n<ul>\\r\\n	<li><span style=\"font-size:14px\">- Atis&ocirc; (Cynara scolymus chiết xuất equiv.&nbsp; 1,0 g l&aacute; tươi)</span></li>\\r\\n	<li><span style=\"font-size:14px\">- Chiết xuất chuẩn h&oacute;a tương đương với dược phẩm kh&ocirc;:</span></li>\\r\\n	<li><span style=\"font-size:14px\">- C&acirc;y kế sữa (Thistle St Mary &ndash; quả Silybum marianum 2,5 g, flavanolignans dưới dạng Silybin 28,6mg )</span></li>\\r\\n	<li><span style=\"font-size:14px\">- Củ nghệ (th&acirc;n rễ c&acirc;y Curcuma longa 1,5 g, curcuminoids 48.75mg)</span></li>\\r\\n</ul>\\r\\n\\r\\n<p><span style=\"font-size:14px\"><em>Kh&ocirc;ng c&oacute;</em><em>&nbsp;th&ecirc;m gluten, lactose, trứng, men, muối, đậu n&agrave;nh, chất bảo quản, m&agrave;u sắc nh&acirc;n tạo v&agrave; hương vị. Th&iacute;ch hợp cho người ăn chay.</em></span></p>\\r\\n\\r\\n<p><span style=\"color:rgb(255, 140, 0)\"><span style=\"font-size:14px\"><strong>Khuyến c&aacute;o:</strong></span></span></p>\\r\\n\\r\\n<ul>\\r\\n	<li><span style=\"font-size:14px\">Kh&ocirc;ng sử dụng nếu c&oacute; thai hoặc c&oacute; khả năng mang thai. Nếu triệu chứng kh&ocirc;ng giảm tham khảo &yacute; kiến ​​b&aacute;c sĩ của bạn</span></li>\\r\\n	<li><span style=\"font-size:14px\">Bảo quản dưới 30 &deg; C</span></li>\\r\\n	<li>&nbsp;</li>\\r\\n</ul>\\r\\n\\r\\n<p><span style=\"color:rgb(255, 140, 0)\"><span style=\"font-size:14px\"><strong>Th&ocirc;ng tin th&ecirc;m:</strong></span></span></p>\\r\\n\\r\\n<p><span style=\"font-size:14px\">Sản phẩm được sản xuất dựa tr&ecirc;n 25 năm nghi&ecirc;n cứu của Swisse. C&aacute;c bằng chứng nghi&ecirc;n cứu về th&agrave;nh phần của sản phẩm:</span></p>\\r\\n\\r\\n<ul>\\r\\n	<li><span style=\"font-size:14px\">Mills &amp; Bone, 2000.&nbsp;<em>Principals and Practice of Phytotherapy.</em>&nbsp;Churchill Livingstone .</span></li>\\r\\n	<li><span style=\"font-size:14px\">Natural Standard Professional Monograph, 2012.&nbsp;<em>Silybum marianum</em>.</span></li>\\r\\n	<li><span style=\"font-size:14px\">Health Canada Monograph -&nbsp;<em>Milk Thistle</em>. 2009.</span></li>\\r\\n	<li><span style=\"font-size:14px\">Kumar &amp; Clarke, 2009.&nbsp;<em>Clinical Medicine, Seventh Edition</em>. Saunders .</span></li>\\r\\n	<li><span style=\"font-size:14px\">Haslett, Ed. 2000.&nbsp;<em>Davidson&#39;s Principles &amp; Practise of Medicine, Eighteenth Edition</em>. Churchill Livingstone.</span></li>\\r\\n	<li><span style=\"font-size:14px\">Gould, 2006. Physiology for Heath Professionals, Third Edition. Saunders Elsevier.</span></li>\\r\\n	<li><span style=\"font-size:14px\">Health Canada Monograph -<em>&nbsp;Turmeric root</em>. 2010.</span></li>\\r\\n	<li><span style=\"font-size:14px\">Health Canada Monograph - Globe artichoke. 2008.</span></li>\\r\\n	<li><span style=\"font-size:14px\">Fisher &amp; Painter. 1996.&nbsp;<em>Materia Medica Western Herbs for the Southern Hemisphere.</em>&nbsp;St Mary&#39;s Thistle monograph</span></li>\\r\\n	<li><span style=\"font-size:14px\">Commission E Monograph - Turmeric Root</span></li>\\r\\n</ul>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n', '04-33-05-08-07-2016-lntvien-uong-bo-gan-va-giai-doc-swisse-liver-detox-120vn540b560.jpg', '04-33-05-08-07-2016-lntvien-uong-bo-gan-va-giai-doc-swisse-liver-detox-120vn540b560.jpg', 'a:1:{i:0;s:87:\"04-33-05-08-07-2016-lntvien-uong-bo-gan-va-giai-doc-swisse-liver-detox-120vn540b560.jpg\";}', '0', null, null, '236', '---------- Thuốc - Sức khỏe', '0', '0', '1', '1', '55', 'Siêu thị Hàng Xách tay', '3', '1', '-1', '0', '22', null, null, '1467970385', null, null, '1489051696');
INSERT INTO `web_product` VALUES ('674', '', 'Uvita giúp làm lành vết viêm loét dạ dày, tá tràng', '360000', '0', '0', '0', '1', '', '1', '<p>Uvita&nbsp;Gi&uacute;p l&agrave;m l&agrave;nh vết lo&eacute;t ở hệ ti&ecirc;u h&oacute;a.&nbsp;ph&ograve;ng ngừa t&aacute;i ph&aacute;t vi&ecirc;m lo&eacute;t dạ d&agrave;y, t&aacute; tr&agrave;ng, đại tr&agrave;ng, l&agrave;m&nbsp;giảm triệu chứng chướng bụng do đầy hơi, ợ n&oacute;ng, ợ chua, kh&oacute; ti&ecirc;u, đau r&aacute;t v&ugrave;ng thượng vị.</p>\\r\\n', '<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong>Th&agrave;nh phần</strong>&nbsp;(trong 1 vi&ecirc;n nang):</span></span></p>\\r\\n\\r\\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">NanoCurcumin,&nbsp;Vitamin U,&nbsp;Carnosin, Rotundin, Vitamin A,&nbsp;VitaminE, MgCO3, CaCO3.</span></span></p>\\r\\n\\r\\n<p style=\"text-align:center\"><img src=\"http://shopcuatui.com.vn/uploads/product/674/05-41-00-08-07-2016-uvita-1.jpg\" /></p>\\r\\n\\r\\n<p style=\"text-align:center\">Uvita - Vitamin u gi&uacute;p l&agrave;m l&agrave;nh vết vi&ecirc;m lo&eacute;t dạ d&agrave;y, t&aacute; tr&agrave;ng.</p>\\r\\n\\r\\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\"><strong>C&ocirc;ng dụng của UVITA:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></span></span></p>\\r\\n\\r\\n<p><span style=\"font-size:14px\"><span style=\"font-family:times new roman,times,serif\">- Gi&uacute;p l&agrave;m giảm vi&ecirc;m lo&eacute;t dạ d&agrave;y, t&aacute; tr&agrave;ng, đại tr&agrave;ng.<br />\\r\\n- Gi&uacute;p l&agrave;m l&agrave;nh vết lo&eacute;t ở hệ ti&ecirc;u h&oacute;a.<br />\\r\\n- Gi&uacute;p ph&ograve;ng ngừa t&aacute;i ph&aacute;t vi&ecirc;m lo&eacute;t dạ d&agrave;y, t&aacute; tr&agrave;ng, đại tr&agrave;ng.<br />\\r\\n- Gi&uacute;p giảm triệu chứng chướng bụng do đầy hơi, ợ n&oacute;ng, ợ chua, kh&oacute; ti&ecirc;u, đau r&aacute;t v