INSERT INTO `web_product` VALUES ('694', null, 'Thịt hộp Hormel Spam Less Sodium 340g (Spam Giảm Mặn)', '85000', '99000', '85000', '0', '1', '', '1', '<p>Thịt hộp Hormel Spam Lite 340g ( Spam Giảm B&eacute;o)</p>\\r\\n\\r\\n<p>Thịt hộp Spam được sản xuất tr&ecirc;n d&acirc;y chuyền c&ocirc;ng nghệ hiện đại, cung cấp những m&oacute;n ăn ngon m&agrave; vẫn đầy đủ dưỡng chất.<br />\\r\\nSản phẩm rất ph&ugrave; hợp cho cuộc sống hiện đại.<br />\\r\\nSản phẩm được sản xuất từ thịt h&ograve;a trộn với c&aacute;c loại gia vị đường, muối đậm đ&agrave;.&nbsp;<br />\\r\\nThịt hộp Spam Lite c&oacute; hương vị thơm ngon, hấp dẫn, c&oacute; thể ăn liền m&agrave; kh&ocirc;ng cần phải th&ecirc;m gia vị.&nbsp;<br />\\r\\nSản phẩm đ&oacute;ng hộp đẹp mắt, tiện dụng, c&oacute; thể d&ugrave;ng ăn k&egrave;m với b&aacute;nh mỳ, salad, pho m&aacute;t, m&igrave; g&oacute;i hoặc b&uacute;n tươi.&nbsp;<br />\\r\\nĐặc biệt hộp nhỏ gọn tiện lợi sử dụng khi bận rộn hay đi du lịch, cắm trại, d&atilde; ngoại, cho bữa ăn của gia đ&igrave;nh th&ecirc;m phong ph&uacute;.</p>\\r\\n', '<p><img src=\"http://shopcuatui.com.vn/uploads/product/694/02-20-44-11-08-2016-138950126353372899771274057837874054465404n.jpg\" /><img src=\"http://shopcuatui.com.vn/uploads/product/694/02-20-54-11-08-2016-138955206353671633074737235965852792666256n.jpg\" /><img src=\"http://shopcuatui.com.vn/uploads/product/694/02-21-02-11-08-2016-500x500.jpg\" /><img src=\"http://shopcuatui.com.vn/uploads/product/694/02-21-13-11-08-2016-139394416353647366410497843334473380308626n.jpg\" /></p>\\r\\n', '02-20-44-11-08-2016-138950126353372899771274057837874054465404n.jpg', '02-20-54-11-08-2016-138955206353671633074737235965852792666256n.jpg', 'a:4:{i:0;s:67:\"02-20-44-11-08-2016-138950126353372899771274057837874054465404n.jpg\";i:1;s:67:\"02-20-54-11-08-2016-138955206353671633074737235965852792666256n.jpg\";i:2;s:31:\"02-21-02-11-08-2016-500x500.jpg\";i:3;s:67:\"02-21-13-11-08-2016-139394416353647366410497843334473380308626n.jpg\";}',