INSERT INTO `web_product` VALUES ('709', '', 'Viên gout az làm tan cục tophi do gout', '260000', '0', '0', '0', '1', '', '1', '<p><strong>Vi&ecirc;n Gout AZ gi&uacute;p&nbsp;</strong>L&agrave;m giảm c&aacute;c triệu chứng đau nhức xương khớp do gout,vi&ecirc;m khớp, giảm axit uric m&aacute;u v&agrave; ph&ograve;ng ngừa cơn gout cấp t&aacute;i ph&aacute;t.<br />\\r\\n<br />\\r\\n&nbsp;</p>\\r\\n', '<p><strong>Vi&ecirc;n Gout AZ gi&uacute;p:</strong>&nbsp;<br />\\r\\n- L&agrave;m giảm c&aacute;c triệu chứng đau nhức xương khớp do gout,vi&ecirc;m khớp<br />\\r\\n- Giảm axit uric m&aacute;u<br />\\r\\n- Ph&ograve;ng ngừa cơn gout cấp t&aacute;i ph&aacute;t<br />\\r\\n- Hỗ trợ l&agrave;m tan cục tophi do gout.<br />\\r\\n<br />\\r\\n<strong>Th&agrave;nh phần:</strong><br />\\r\\n- Cao kh&ocirc; Cơm lệnh &hellip;.&hellip;100mg<br />\\r\\n- Cao kh&ocirc; Cao cẳng &hellip;.&hellip;100mg<br />\\r\\n- Hỗn hợp cao kh&ocirc; dược liệu 400 mg tương đương với:<br />\\r\\n+ S&oacute;i rừng&hellip;&hellip;...1500mg<br />\\r\\n+ Mỏ quạ&hellip;&hellip;&hellip;..1500mg<br />\\r\\n+ Chuối hột&hellip;&hellip;1000mg<br />\\r\\n+ Củ r&aacute;y&hellip;&hellip;.&hellip;1000mg<br />\\r\\n+ Nghệ v&agrave;ng&hellip;.&hellip;1000mg<br />\\r\\nPhụ liệu: Tinh bột, lactose, magnesi stearat vừa đủ 1 vi&ecirc;n.<br />\\r\\n<br />\\r\\n<strong>Cơ chế t&aacute;c dụng:</strong><br />\\r\\n-Gout l&agrave; bệnh rối loạn chuyển h&oacute;a nh&acirc;n purin g&acirc;y tăng axit uric m&aacute;u l&agrave;m vi&ecirc;m khớp do vi tinh thể đặc trưng bởi đợt vi&ecirc;m khớp cấp t&aacute;i ph&aacute;t c&oacute; lắng đọng tinh thể muối urat trong c&aacute;c m&ocirc;.<br />\\r\\nSản phẩm Gout AZ chiết xuất 100% từ thảo dược thi&ecirc;n nhi&ecirc;n như cao kh&ocirc; Cơm lệnh (s&acirc;m đất),Cao kh&ocirc; Cao cẳng, mỏ quạ ,nghệ&hellip;c&oacute; t&aacute;c dụng hoạt huyết, th&ocirc;ng kinh lạc gi&uacute;p giảm cơn đau Gout cấp,chống vi&ecirc;m hiệu quả,an to&agrave;n.Cơm lệnh (s&acirc;m đất) ức chế enzym xanthine oxidase l&agrave;m hạ axit uric m&aacute;u đưa về chỉ số b&igrave;nh thường.Th&agrave;nh phần Cao cẳng, nghệ v&agrave;ng chứa hoạt chất flavonoid gi&uacute;p chống oxy h&oacute;a ,bảo vệ tế b&agrave;o sụn khớp.<br />\\r\\n<br />\\r\\n<strong>Đối tượng sử dụng:</strong>&nbsp;<br />\\r\\n- D&ugrave;ng cho người bị bệnh gout<br />\\r\\n- Người c&oacute; nguy cơ cao mắc bệnh gout.<br />\\r\\n<br />\\r\\n<strong>Ki&ecirc;ng kỵ:</strong><br />\\r\\n- Thức ăn chứa nh&acirc;n purin : thịt b&ograve;, thịt tr&acirc;u, thịt lợn, nội tạng động vật, l&ograve;ng đỏ trứng, nấm, măng t&acirc;y.<br />\\r\\n- Hạn chế tối đa rượu, bia.<br />\\r\\n- Tr&aacute;nh c&aacute;c yếu tố khởi ph&aacute;t cơn gout cấp: stress, chấn thương, lạnh.<br />\\r\\n<br />\\r\\n<strong>Lời khuy&ecirc;n</strong><br />\\r\\n- Tập luyện thể dục điều độ, giảm c&acirc;n gi&uacute;p giảm &aacute;p lực cho khớp<br />\\r\\n- Uống nhiều nước khoảng 2-4l/24h đặc biệt l&agrave; c&aacute;c loại nước kho&aacute;ng c&oacute; kiềm hoặc nước kiềm 1,4%.<br />\\r\\n<br />\\r\\n<strong>Hướng dẫn sử dụng:&nbsp;</strong><br />\\r\\n- Người mắc bệnh gout: Uống 2 vi&ecirc;n/lần x 2 lần/ng&agrave;y v&agrave;o l&uacute;c 8h s&aacute;ng v&agrave; 12h trưa sử dụng li&ecirc;n tục trong 6 th&aacute;ng.Sau đ&oacute; duy tr&igrave; uống 1 vi&ecirc;n/lần 2 lần /ng&agrave;y<br />\\r\\n- Người c&oacute; nguy cơ mắc bệnh gout : uống 1 vi&ecirc;n/lần 2 lần/ng&agrave;y&nbsp;<br />\\r\\nSản phẩm chiết xuất từ thảo dược n&ecirc;n rất an to&agrave;n c&oacute; thể sử dụng h&agrave;ng ng&agrave;y.<br />\\r\\n<br />\\r\\n<strong>Bảo quản:</strong> Bảo quản nơi kh&ocirc; r&aacute;o c&oacute; nhiệt độ dưới 30&ordm;C, tr&aacute;nh &aacute;nh nắng trực tiếp.<br />\\r\\n<strong>Quy c&aacute;ch</strong>: Hộp 30 vi&ecirc;n.<br />\\r\\n<strong>Gi&aacute; b&aacute;n</strong>: 260.000đ/ hộp.<br />\\r\\n<br />\\r\\nSố ĐK: 19262/2016/ATTP-XNCB<br />\\r\\nHSD: 36 th&aacute;ng kể từ ng&agrave;y sản xuất. Nsx &amp; Hsd in tr&ecirc;n bao b&igrave;.<br />\\r\\nSản xuất