INSERT INTO `web_product` VALUES ('774', '', 'Bột tẩy lồng máy giặt', '45000', '50000', '0', '0', '1', '', '1', '<p><span style=\\\"color:rgb(29, 33, 41)\\\">Bột tẩy lồng&nbsp;</span><span style=\\\"color:rgb(29, 33, 41)\\\">m&aacute;y giặt H&agrave;n Quốc</span><br />\\r\\n<span style=\\\"color:rgb(29, 33, 41)\\\">- Loại bỏ những cặn lắng caxi v&agrave; chất bẩn c&aacute;u cặn , diệt khuẩn g&acirc;y m&ugrave;i h&ocirc;i trong m&aacute;y giặt v&agrave; đường ống th&oacute;at nứớc.</span></p>\\r\\n', '<div class=\"text_exposed_root text_exposed\" id=\"id_5800af8bab2406096949188\" style=\"display: inline; font-family: inherit;\"><span style=\"color:rgb(29, 33, 41); font-family:helvetica,arial,sans-serif; font-size:14px\"><span style=\"font-family:inherit\">Bột tẩy lồng&nbsp;m&aacute;y giặt<br />\\r\\n- Loại bỏ những cặn lắng caxi v&agrave; chất bẩn c&aacute;u cặn , diệt khuẩn g&acirc;y m&ugrave;i h&ocirc;i trong m&aacute;y giặt v&agrave; đường ống th&oacute;at nứớc.<br />\\r\\n- Tăng cường họat chất, tẩy giặt gi&uacute;p bạn giặt sạch hơn m&agrave; kh&ocirc;ng lo về sự cố do c&aacute;c t&aacute;c nh&acirc;n c&aacute;u cặn l&acirc;u ng&agrave;y kh&ocirc;ng được sử l&yacute; g&acirc;y n&ecirc;n.<br />\\r\\n- Bạn ho&agrave;n to&agrave;n kh&ocirc;ng phải bảo dưỡng m&aacute;y giặt khi sử dụng sản phẩm n&agrave;y.<br />\\r\\n- Trọng lượng: 450gr/g&oacute;i.<br />\\r\\n<span style=\"font-family:inherit\">- HSD: 3 năm kể từ ng&agrave;y sản xuất.<br />\\r\\n- Kh&ocirc;ng cần đến thợ th&aacute;o m&aacute;y giặt ra để vệ sinh m&agrave; nay bất kỳ ai cũng c&oacute; thể tự l&agrave;m được ở nh&agrave;.<br />\\r\\n- D&ugrave;ng được cho cả m&aacute;y giặt lồng đứng v&agrave; m&aacute;y giặt lồng ngang.<br />\\r\\n- 1 g&oacute;i 450gr sử dụng cho 1 lần vệ sinh với m&aacute;y giặt từ 7kg ~ 12kg. M&aacute;y giặt to hơn th&igrave; d&ugrave;ng 2 g&oacute;i.<br />\\r\\nHướng dẫn sử dụng<br />\\r\\n- Bỏ hết đồ trong lồng giặt ra- đảm bảo m&aacute;y giặt kh&ocirc;ng chứa đồ, quần &aacute;o