INSERT INTO `web_product` VALUES ('792', '', 'Thùng đựng gạo thông minh GD22W (22KG)', '1250000', '0', '0', '0', '1', '', '1', '<p><strong>Th&ugrave;ng đựng gạo th&ocirc;ng minh&nbsp;GD22W</strong><span style=\\\"color:rgb(102, 102, 102)\\\">&nbsp;được nhập khẩu nguy&ecirc;n chiếc từ H&agrave;n Quốc l&agrave;m bằng inox phủ sơn tĩnh điện &nbsp;đựng&nbsp;tối đa 22kg&nbsp;gạo kh&ocirc;ng bị nấm mốc, v&agrave; đặc biệt kh&ocirc;ng g&acirc;y độc hại khi sử dụng gạo. Với thiết kế 2 n&uacute;t ấn điều khiển tiện dụng, chỉ việc ấn xuống gạo sẽ tự động chảy xuống một lượng nhất định l&agrave; 150g hoặc 300g dễ d&agrave;ng cho người sử dụng v&agrave; nhanh gọn hơn trong nấu cơm cho c&aacute;c gia đ&igrave;nh.</span></p>\\r\\n\\r\\n<p><strong>Nắp đổ tiện lợi:</strong></p>\\r\\n\\r\\n<p>Th&ugrave;ng gạo th&ocirc;ng minh GD22W c&oacute; khay đổ gạo từ ph&iacute;a tr&ecirc;n, bạn kh&ocirc;ng phải mở nắp để đổ gạo v&agrave;o th&ugrave;ng chứa, bạn chỉ cần mở kho&aacute; ph&iacute;a tr&ecirc;n, khay đổ gạo sẽ từ từ ngả xuống v&agrave; bạn chỉ việc đổ gạo v&agrave;o, khi đổ gạo xong bạn chỉ cần đ&oacute;ng khay lại v&agrave; đ&oacute;ng kho&aacute; nắp.</p>\\r\\n\\r\\n<p><strong>&uacute;t Ấn Th&ocirc;ng Minh</strong>:C&oacute; hai n&uacute;t ấn đong gạo, n&uacute;t một&nbsp; 150g, n&uacute;t hai 300g. Với c&aacute;c n&uacute;t ấn th&ocirc;ng minh, bạn c&oacute; thể tự ước lượng số gạo cần nấu m&agrave; kh&ocirc;ng phải đong đo phức tạp. Mặt kh&aacute;c, bạn kh&ocirc;ng cần cho tay hay mở nắp th&ugrave;ng gạo ra để đong gạo khi đang bận nấu ăn, do đ&oacute; c&oacute; thể bảo quản được gạo kh&ocirc;ng bị ướt hoặc ẩm mốc v&agrave; giữ được m&ugrave;i hương của gạo.</p>\\r\\n\\r\\n<p><strong>Cửa Sổ Kiểm Tra:</strong>Cửa sổ kiểm tra gạo,độc đ&aacute;o, dễ kiểm tra lượng gạo trong th&ugrave;ng m&agrave; kh&ocirc;ng cần mở nắp ra</p>\\r\\n\\r\\n<p><strong>Khay Lấy gạo:</strong>&nbsp;Nhỏ gọn,c&oacute; quai cầm dễ d&agrave;ng khi lấy gạo, k&iacute;n kẽ ngăn ngừa mọi c&ocirc;n tr&ugrave;ng v&agrave;o</p>\\r\\n\\r\\n<p>Với c&ocirc;ng nghệ th&eacute;p chống gỉ (inxox) v&agrave; nhựa ABS l&agrave; loại nhựa c&oacute; độ dẻo v&agrave; chống va đập cực tốt, chống trầy xước, chống ẩm mốc v&agrave; c&aacute;c loại c&ocirc;n tr&ugrave;ng, c&aacute;c loại gặm nhấm như chuột, gi&aacute;n&hellip; n&ecirc;n tuyệt đối an to&agrave;n đối với thực phẩm đựng trong th&ugrave;ng.</p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p><strong>Th&ugrave;ng đựng gạo th&ocirc;ng minh GD22W(22kg)</strong>&nbsp;c&oacute; kiểu d&aacute;ng nhỏ gọn với hai b&aacute;nh xe đẩy tiện dụng gi&uacute;p bạn dễ d&agrave;ng di chuyển th&ugrave;ng trong ph&ograve;ng bếp.</p>\\r\\n\\r\\n<p>Với những t&iacute;nh năng tr&ecirc;n th&igrave;&nbsp;<strong>th&ugrave;ng đựng gạo th&ocirc;ng minh GD22W(22kg)</strong>&nbsp;xứng đ&aacute;ng l&agrave; lựa chọn số 1 của c&aacute;c b&agrave; nội trợ, l&agrave; bạn đồng h&agrave;nh kh&ocirc;ng thể thiếu với gia định bạn g&oacute;p phần tạo n&ecirc;n những bữa cơm ngon miệng v&agrave; an to&agrave;n cho mọi th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh bạn.</p>\\r\\n\\r\\n<p>Th&ocirc;ng số kỹ thuật</p>\\r\\n\\r\\n<p>M&atilde; sản phẩm: GD22W (22kg)</p>\\r\\n\\r\\n<p>K&iacute;ch thước : 190 x 380 x 630 mm (R&ocirc;ng-D&agrave;i-Cao)</p>\\r\\n\\r\\n<p>Dung t&iacute;ch b&igrave;nh chứa: 22 Kg</p>\\r\\n\\r\\n<p>Xuất xứ: H&agrave;n quốc - made in Korea</p>\\r\\n\\r\\n<p>Chất liệu: inox, sơn tĩnh điện</p>\\r\\n\\r\\n<p>Nguy&ecirc;n l&yacute; hoạt động: 2 n&uacute;t ấn tiện lợi 150g v&agrave; 300g</p>\\r\\n\\r\\n<p>Bảo h&agrave;nh 2 năm,c&oacute; linh kiện thay thế</p>\\r\\n', '<p><img alt=\"Thùng đựng gạo thông minh GD22W (22KG)\" src=\"http://shopcuatui.com.vn/uploads/thumbs/product/792/600x600/1476949607-5156thung-dung-gao-samick-w22-1.jpg\" /><img alt=\"null\" src=\"http://shopcuatui.com.vn/uploads/thumbs/product/792/600x600/1476949555-thung-dung-gao-samick-w32-2.jpg\" /></p>\\r\\n', '1476949607-5156thung-dung-gao-samick-w22-1.jpg', '1476949607-5156thung-dung-gao-samick-w22-1.jpg', 'a:1:{i:0;s:46:\"1476949607-5156thung-dung-gao-samick-w22-1.jpg\";}', '0', null, null, '153', 'Dụng cụ nhà bếp', '0', '0', '1', '1', '74', 'Đồ Gia Dụng ', '1', '1', '0', '0', '22', null, null, '1476949555', null, null, '1476950239');
INSERT INTO `web_product` VALUES ('793', '', 'Nước Đông trùng Hạ Thảo', '0', '0', '0', '0', '2', '', '1', '<p>Một số nghi&ecirc;n cứu hiện đại chỉ ra rằng&nbsp;<strong>đ&ocirc;ng tr&ugrave;ng hạ thảo</strong>&nbsp;c&oacute; t&aacute;c dụng tăng cường c&ocirc;ng năng của tuyến thượng thận, cải thiện chức năng thận, n&acirc;ng cao năng lực miễn dịch, kh&aacute;ng khuẩn, kh&aacute;ng virus, chống ung thư v&agrave; ph&oacute;ng xạ.</p>\\r\\n\\r\\n<p><strong>Nước uống Đ&ocirc;ng Tr&ugrave;ng Hạ Thảo</strong>&nbsp;được chiết xuất từ đ&ocirc;ng tr&ugrave;ng hạ thảo thi&ecirc;n nhi&ecirc;n, với vị thơm hấp dẫn đễ uống, ngo&agrave;i t&aacute;c dụng giải kh&aacute;t c&ograve;n c&oacute; t&aacute;c dụng giảm stress, bổ sung năng lượng cho cơ thể khỏe mạnh.&nbsp;<strong>Nước uống Đ&ocirc;ng Tr&ugrave;ng Hạ Thảo</strong>&nbsp;c&oacute; m&agrave;u v&agrave;ng nhạt vị chua ngọt, được đ&oacute;ng chai sang trọng v&agrave; đảm bảo vệ sinh rất tốt cho sức khỏe của con người.</p>\\r\\n\\r\\n<p><strong>Th&agrave;nh phần c&oacute; trong&nbsp;nước uống đ&ocirc;ng tr&ugrave;ng hạ thảo</strong>: Chiết xuất từ Đ&ocirc;ng Tr&ugrave;ng Hạ Thảo, mật ong, vitamin B2, cam thảo, nấm linh chi, quả t&aacute;o t&agrave;u, Stevioside, Axit citric, Fructose dạng lỏng, Nicotine amide, Natri benzoat, Natri citrate, nước.</p>\\r\\n\\r\\n<p><strong>C&ocirc;ng dụng của nước uống đ&ocirc;ng tr&ugrave;ng hạ thảo h&agrave;n quốc</strong>:</p>\\r\\n\\r\\n<p>- Gi&uacute;p bồi bổ sức khỏe</p>\\r\\n\\r\\n<p>- N&acirc;ng cao sức đề kh&aacute;ng</p>\\r\\n\\r\\n<p>- Cải thiện chức năng của thận v&agrave; hệ tim mạch</p>\\r\\n\\r\\n<p>- Tăng cường khả năng sinh l&yacute;</p>\\r\\n\\r\\n<p><strong>C&aacute;ch d&ugrave;ng</strong>:</p>\\r\\n\\r\\n<p>- Người lớn:&nbsp;mỗi ng&agrave;y uống từ 1-2 chai hoặc t&ugrave;y theo nhu cầu</p>\\r\\n\\r\\n<p>- Trẻ em từ 1-10 tuổi: mỗi ng&agrave;y uống 1 chai, mỗi lần uống 50ml, chia l&agrave;m 2 lần</p>\\r\\n\\r\\n<p>- Uống ngay sau khi mở nắp, n&ecirc;n để lạnh v&agrave; lắc nhẹ trước khi dung sẽ ngon hơn.</p>\\r\\n\\r\\n<p>- Kh&ocirc;ng d&ugrave;ng cho trẻ em dưới 1 tuổi</p>\\r\\n\\r\\n<p><strong>Ch&uacute; &yacute;</strong>:&nbsp;<em>Sản phẩm n&agrave;y kh&ocirc;ng phải l&agrave; thuốc v&agrave; kh&ocirc;ng c&oacute; t&aacute;c dụng thay thế thuốc chữa bệnh</em></p>\\r\\n\\r\\n<p>Th&ocirc;ng số kỹ thuật</p>\\r\\n\\r\\n<p><strong>-</strong><strong>&nbsp;T&ecirc;n sản phẩm:&nbsp;</strong>Nước Uống Đ&ocirc;ng Tr&ugrave;ng Hạ Thảo Dạng Chai (Hộp m&agrave;u đen)</p>\\r\\n\\r\\n<p><strong>- Quy c&aacute;ch đ&oacute;ng g&oacute;i</strong>: Hộp 10 chai , mỗi chai 100 ml</p>\\r\\n\\r\\n<p><strong>- Bảo quản</strong>: Bảo quản ở nhiệt độ ph&ograve;ng (dưới 30oC), tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\\r\\n\\r\\n<p><strong>- Xuất xứ</strong>: Korea Ginseng Bio - Science Co Ltd (H&agrave;n Quốc)</p>\\r\\n\\r\\n<p><strong>- Số l&ocirc;,</strong>&nbsp;<strong>NSX</strong>,&nbsp;<strong>HSD</strong>: Xem tr&ecirc;n nh&atilde;n chai</p>\\r\\n', '<p><img alt=\"null\" src=\"http://shopcuatui.com.vn/uploads/thumbs/product/793/600x600/1476949796-1013dong-trung-ha-thao-bo-than-phoi2012912153720943.jpg\" /></p>\\r\\n', '1476949796-1013dong-trung-ha-thao-bo-than-phoi2012912153720943.jpg', '1476949796-1013dong-trung-ha-thao-bo-than-phoi2012912153720943.jpg', 'a:1:{i:0;s:66:\"1476949796-1013dong-trung-ha-thao-bo-than-phoi2012912153720943.jpg\";}', '0', null, null, '196', 'Thực phẩm chức năng', '0', '0', '1', '1', '74', 'Đồ Gia Dụng ', '1', '1', '0', '0', '22', null, null, '1476949796', null, null, '1476949983');
