INSERT INTO `web_product` VALUES ('823', '', 'Bộ chăm sóc tóc chuyên nghiệp PRINCESS WHITE', '350000', '360000', '0', '0', '1', '', '1', '<p><span style=\\\"background-color:rgba(255, 255, 255, 0.298039); color:rgb(57, 70, 79)\\\">BỘ CHĂM S&Oacute;C T&Oacute;C T&Aacute;I CẤU TR&Uacute;C HƯ TỔN &ndash; PRINCESS WHITE l&agrave; liệu ph&aacute;p dưỡng ẩm chuy&ecirc;n s&acirc;u d&agrave;nh cho m&aacute;i t&oacute;c dễ xơ rối trở n&ecirc;n su&ocirc;n mượt theo đ&uacute;ng &lsquo;CHUẨN SALON&rsquo;.</span></p>\\r\\n', '<p><span style=\"font-family:arial,helvetica,sans-serif\"><strong>BỘ CHĂM S&Oacute;C T&Oacute;C T&Aacute;I CẤU TR&Uacute;C HƯ TỔN &ndash; PRINCESS WHITE</strong></span></p>\\r\\n\\r\\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">L&agrave; phụ nữ, ai cũng muốn sở hữu m&aacute;i t&oacute;c su&ocirc;n d&agrave;i mềm mượt. Sau những lần thay đổi phong c&aacute;ch cho m&aacute;i t&oacute;c bằng c&aacute;c liệu ph&aacute;p uốn, &eacute;p, nhuộm, m&aacute;i t&oacute;c của nhiều chị em bị hư tổn nghi&ecirc;m trọng với c&aacute;c triệu chứng xơ, rối, kh&ocirc; hay chẻ ngọn.&nbsp;Ch&iacute;nh v&igrave; thế m&agrave; việc sử dụng c&aacute;c sản phẩm chăm s&oacute;c t&oacute;c ph&ograve;ng ngừa &ndash; chữa trị t&oacute;c hư tổn l&agrave; việc l&agrave;m kh&ocirc;ng thể thiếu với c&aacute;c chị em.</span></p>\\r\\n\\r\\n<p style=\"text-align:justify\"><span style=\"font-family:arial,helvetica,sans-serif\">Nhằm mang đến vẻ đẹp ho&agrave;n hảo cho phụ nữ, lần đầu ti&ecirc;n Princess White đ&atilde; nghi&ecirc;n cứu v&agrave; &aacute;p dụng&nbsp;<em>c&ocirc;ng nghệ&nbsp;<strong>Lipidium</strong>&nbsp;t&aacute;i tạo bề mặt sợi t&oacute;c, củng cố cấu tr&uacute;c t&oacute;c, c&oacute; khả năng phục hồi c&aacute;c li&ecirc;n kết b&ecirc;n trong t&oacute;c cho kết quả t&oacute;c mềm mượt v&agrave; chắc khoẻ để&nbsp;</em>đưa ra thị trường d&ograve;ng sản phẩm&nbsp;<strong>PHỤC HỒI T&Oacute;C &ndash; GI&Uacute;P T&Oacute;C SU&Ocirc;N MƯ