INSERT INTO `web_product` VALUES ('902', '', 'Nước tẩy trang Garnier Micellar Công thức mới dành cho da nhạy cảm ', '280000', '0', '200', '280000', '1', '', '2', '<p>- TUYỆT ĐỐI kh&ocirc;ng l&agrave;m kh&ocirc; da v&agrave; g&acirc;y k&iacute;ch ứng, thậm ch&iacute; l&agrave;m mềm da v&agrave; duy tr&igrave; độ ẩm vốn c&oacute; của da</p>\r\n\r\n<p>&lt;span style=&quot;\\\\\\\\&amp;quot;color:rgb(0,&quot; 0,=&quot;&quot; 0)\\\\\\\\&quot;=&quot;&quot;&gt;- Sản phẩm kh&ocirc;ng g&acirc;y k&iacute;ch ứng với da nhạy cảm như nước tẩy trang chứa cồn.</p>\r\n', '<h2 style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><strong><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Nước tẩy trang Garnier Micellar C&ocirc;ng thức mới&nbsp;d&agrave;nh cho da nhạy cảm&nbsp;</span></span></strong></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Xuất xứ: Made in French (h&agrave;ng Nga nhập khẩu)</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Dung t&iacute;ch: 400ml</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Nh&agrave; sản xuất: Garnier French</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"null\" src=\"http://shopcuatui.com.vn/uploads/thumbs/product/902/600x600/1486888907-image.jpg\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Nước tẩy trang Garnier Micellar Cleansing water l&agrave; d&ograve;ng sản phẩm tẩy trang thế hệ mới ứng dụng c&ocirc;ng nghệ Micellar Technology của Garnier laboratory. Với c&ocirc;ng nghệ Micellar, nước tẩy trang của Garnier KH&Ocirc;NG D&Ugrave;NG CỒN&nbsp;để l&agrave;m sạch&nbsp;lấy đi cặn trang điểm m&agrave; ứng dụng cấu tr&uacute;c ph&acirc;n tử c&oacute; đầu ưa nước v&agrave; đu&ocirc;i kị nước để h&uacute;t cặn trang điểm v&agrave; bụi bẩn trong lỗ ch&acirc;n l&ocirc;ng. Khi b&ocirc;i l&ecirc;n da, c&aacute;c micell mở ra, h&uacute;t tất cả bụi bẩn, cặn trang điểm của lớp makeup v&agrave; mang n&oacute; đi khi bạn quệt b&ocirc;ng thấm. Nhờ cơ chế n&agrave;y, da được l&agrave;m sạch tuyệt đối m&agrave; kh&ocirc;ng bị kh&ocirc;, mất nước như c&aacute;c loại nước tẩy trang d&ugrave;ng cồn trước đ&acirc;y.</span></span></span><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Nhờ ưu điểm vượt trội của c&ocirc;ng nghệ Micellar, nước tẩy trang Garnier c&oacute; ưu điểm l&agrave; tẩy được mọi chỗ c&oacute; make-up, kể cả những v&ugrave;ng da cực kỳ nhạy cảm như quanh mắt v&agrave; v&ugrave;ng da m&ocirc;i. Với c&aacute;c loại nước tẩy trang d&ugrave;ng CỒN, sau khi tẩy trang xong bạn c&oacute; cảm gi&aacute;c m&aacute;t lạnh da (do cồn bay hơi lấy đi nhiệt lượng), sau đ&oacute; l&agrave; cảm gi&aacute;c kh&ocirc; da do mất nước, đặc biệt cảm gi&aacute;c kh&ocirc; m&ocirc;i rất kh&oacute; chịu; v&agrave; khi tẩy trang v&ugrave;ng mắt sẽ cảm thấy x&oacute;t nếu&nbsp;bị rớt v&agrave;o.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Với c&ocirc;ng nghệ Micellar của Garnier th&igrave; điều đ&oacute; kh&ocirc;ng c&ograve;n nữa, việc tẩy son m&ocirc;i v&agrave; kẻ mắt kh&ocirc;ng những dễ d&agrave;ng hơn nhiều m&agrave; c&ograve;n rất dễ chịu nữa. Ngay cả khi bạn c&oacute; 1 l&agrave;n da cực kỳ nhạy cảm th&igrave; việc tẩy trang sẽ kh&ocirc;ng c&ograve;n l&agrave; vấn đề.&nbsp;Đ&acirc;y ch&iacute;nh l&agrave; thế mạnh khiến Micellar water hơn hẳn những loại tẩy trang th&ocirc;ng thường</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Garnier n&oacute;i rằng với tẩy trang Micellar water, sau khi tẩy trang bạn kh&ocirc;ng cần thiết phải rửa lại bằng nước. Tuy nhi&ecirc;n nếu cẩn thận hơn, bạn cũng c&oacute; thể rửa mặt bằng sữa rửa mặt ph&ugrave; hợp với da, sau đ&oacute; chăm s&oacute;c da theo thứ tự h&agrave;ng ng&agrave;y của bạn.</span></span></span></p>\r\n\r\n<h2 style=\"text-align:justify\"><img alt=\"null\" src=\"http://shopcuatui.com.vn/uploads/thumbs/product/902/600x600/1486888945-image.jpg\" /></h2>\r\n\r\n<h2 style=\"text-align:justify\"><img alt=\"Nước tẩy trang Garnier Micellar Công thức mới dành cho da nhạy cảm \" src=\"http://shopcuatui.com.vn/uploads/thumbs/product/902/600x600/1486889087-image.jpg\" /></h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><strong><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Nước tẩy trang Garnier Micellar&nbsp;đặc biệt an to&agrave;n, kh&ocirc;ng g&acirc;y k&iacute;ch ứng th&acirc;n thiện với l&agrave;n da nhạy cảm</span></span></strong></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Nước tẩy trang Garnier Micellar d&agrave;nh cho da nhạy cảm&nbsp;c&oacute; thiết kế chai nhựa trong suốt, dung t&iacute;ch lớn 400ml. Sản phẩm l&agrave; dạng dung dịch kh&ocirc;ng m&ugrave;i (no perfume - kh&ocirc;ng chứa nước hoa), d&ugrave;ng tẩy trang v&ugrave;ng da mặt, cổ, m&ocirc;i v&agrave; cả mắt.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- Nhẹ nh&agrave;ng l&agrave;m sạch lớp trang điểm, lấy đi cặn trang điểm cực kỳ nhanh ch&oacute;ng</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- TUYỆT ĐỐI kh&ocirc;ng l&agrave;m kh&ocirc; da v&agrave; g&acirc;y k&iacute;ch ứng, thậm ch&iacute; l&agrave;m mềm da v&agrave; duy tr&igrave; độ ẩm vốn c&oacute; của da</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- Sản phẩm kh&ocirc;ng g&acirc;y k&iacute;ch ứng với da nhạy cảm như nước tẩy trang chứa cồn.</span></span></span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"color:rgb(255, 0, 0)\"><strong><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Sử dụng nước tẩy trang Garnier</span></span></strong></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- Thấm dung dịch nước tẩy trang Garnier Micellar ra b&ocirc;ng tẩy trang cotton</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- Lau l&ecirc;n v&ugrave;ng da make up để ph&acirc;n tử micellar nhanh ch&oacute;ng lấy đi cặn trang điểm, bụi bẩn v&agrave; b&atilde; nhờn dưới da</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- C&oacute; thể kh&ocirc;ng cần rửa lại với nước, tuy nhi&ecirc;n để chăm s&oacute;c da tốt nhất bạn n&ecirc;n rửa với sữa rửa mặt ph&ugrave; hợp, sau đ&oacute; tiếp tục c&aacute;c bước chăm s&oacute;c da thường ng&agrave;y</span></span></span></p>\r\n', '1486888907-image.jpg', '1486888945-image.jpg', 'a:3:{i:0;s:20:\"1486888907-image.jpg\";i:1;s:20:\"1486888945-image.jpg\";i:2;s:20:\"1486889087-image.jpg\";}', '0', null, null, '242', '---------- Mỹ phẩm - Đồ trang điểm', '0', '0', '1', '1', '55', 'Siêu thị Hàng Xách tay', '3', '1', '4', '0', '22', null, null, '1486888906', null, null, '1488688699');
