INSERT INTO `web_product` VALUES ('929', '', 'Son trị muỗi và côn trùng cắn cho trẻ em - Nga', '90000', '0', '0', '90000', '1', '', '1', '<p><strong><span style=\\\"color:rgb(255, 0, 0)\\\">Sản phẩm kem b&ocirc;i&nbsp;muỗi đốt - c&ocirc;n tr&ugrave;ng cắn của Nga</span></strong></p>\r\n\r\n<p><span style=\\\"color:rgb(0, 0, 0)\\\">Kem chống&nbsp;muỗi đốt, c&ocirc;n tr&ugrave;ng cắn dạng son của Nga c&oacute; t&aacute;c dụng đặc biệt trong việc giảm sưng tấy, l&agrave;m hết ngứa ng&aacute;y, ngăn ngừa nhiễm tr&ugrave;ng da của b&eacute;. Sản phẩm&nbsp;an to&agrave;n kh&ocirc;ng g&acirc;y k&iacute;ch ứng, c&oacute; thể d&ugrave;ng được cho trẻ sơ sinh v&agrave; trẻ nhỏ (*)&nbsp;Biểu tượng từ 0 tuổi trở l&ecirc;n tr&ecirc;n bao b&igrave; sản phẩm</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><span style=\\\"color:rgb(255, 0, 0)\\\">Sử dụng son muỗi đốt - c&ocirc;n tr&ugrave;ng cắn hiệu quả</span></strong></p>\r\n\r\n<p><span style=\\\"color:rgb(0, 0, 0)\\\">- Sử dụng tốt nhất ngay sau khi b&eacute; bị muỗi đốt hoặc c&ocirc;n tr&ugrave;ng cắn. Nếu b&ocirc;i ngay trong 5 ph&uacute;t đầu&nbsp;vết cắn sẽ kh&ocirc;ng mẩn ngứa v&agrave; sẽ dịu hẳn, b&eacute; kh&ocirc;ng g&atilde;i n&ecirc;n sẽ kh&ocirc;ng trầy xước g&acirc;y vi&ecirc;m nhiễm.</span></p>\r\n\r\n<p><span style=\\\"color:rgb(0, 0, 0)\\\">- Nếu ph&aacute;t hiện vết cắn muộn, hoặc do muỗi độc hay c&aacute;c loại c&ocirc;n tr&ugrave;ng lạ&nbsp;cắn (vết đốt sưng to) cần b&ocirc;i ngay khi ph&aacute;t hiện, v&agrave; ngăn kh&ocirc;ng cho b&eacute; g&atilde;i v&agrave;o vết cắn. C&aacute;ch khoảng 20 ph&uacute;t lại b&ocirc;i 1 lần, duy tr&igrave; sau đ&oacute; 2 đến 3 lần cho đến khi vết cắn hết ngứa.</span></p>\r\n\r\n<p><span style=\\\"color:rgb(0, 0, 0)\\\">- Nếu vết cắn do c&aacute;c con vật&nbsp;c&oacute; nọc độc như nhện độc, bọ cạp, rết... v&agrave; c&oacute; dấu hiệu nặng (sưng to, t&iacute;m bầm, đau đớn...)&nbsp;cần rửa sạch vết cắn với x&agrave; ph&ograve;ng, đưa đến cơ sở y tế để theo d&otilde;i.</span></p>\r\n\r\n<p><span style=\\\"color:rgb(0, 0, 0)\\\">Son b&ocirc;i vết muỗi đốt được thiết kế dạng thỏi son, tiện lợi khi mang theo v&agrave; sử dụng. Đặc điểm dễ nhận thấy l&agrave; thiết kế kh&aacute; xấu, đ&uacute;ng chất Nga, nhưng d&ugrave;ng tốt cực kỳ (mẹ n&agrave;o d&ugrave;ng son dưỡng m&ocirc;i m&ugrave;a đ&ocirc;ng cho trẻ rồi th&igrave; n&oacute; tr&ocirc;ng xấu gần như thế ạ hhihi)</span></p>\r\n\r\n<p><span style=\\\"color:rgb(255, 0, 0)\\\"><strong>Th&agrave;nh phần:&nbsp;</strong></span><span style=\\\"color:rgb(0, 0, 0)\\\">Sản phẩm kh&ocirc;ng chứa phẩm m&agrave;u, h&oacute;a chất, paraben v&agrave; nước hoa. C&ocirc;ng thức an to&agrave;n đạt ti&ecirc;u chuẩn c&ugrave;ng&nbsp;với sự kết hợp của chiết xuất Argan từ hoa c&uacute;c v&agrave; chuối, dầu bạc h&agrave; v&agrave; chiết xuất ngũ cốc.</span></p>\r\n\r\n<p><em><span style=\\\"color:rgb(0, 0, 205)\\\">Son b&ocirc;i vết&nbsp;muỗi đốt - c&ocirc;n tr&ugrave;ng cắn cực kỳ hiệu quả của Nga</span></em></p>\r\n\r\n<p><br />\r\n<br />\r\n* Sản phẩm an to&agrave;n cho trẻ sơ sinh v&agrave; trẻ nhỏ. Mức độ v&agrave; thời gian hiệu quả c&oacute; thể kh&aacute;c nhau t&ugrave;y theo t&igrave;nh trạng vết cắn v&agrave; cơ địa da</p>\r\n', '<h2 style=\"text-align:justify\"><img alt=\"null\" src=\"http://shopcuatui.com.vn/uploads/thumbs/product/929/600x600/1489213906-image.jpg\" /><strong><span style=\"color:rgb(255, 0, 0)\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Sản phẩm kem b&ocirc;i&nbsp;muỗi đốt - c&ocirc;n tr&ugrave;ng cắn của Nga</span></span></span></strong></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:arial,helvetica,sans-serif; font-size:16px\">Kem chống&nbsp;muỗi đốt, c&ocirc;n tr&ugrave;ng cắn dạng son của Nga c&oacute; t&aacute;c dụng đặc biệt trong việc giảm sưng tấy, l&agrave;m hết ngứa ng&aacute;y, ngăn ngừa nhiễm tr&ugrave;ng da của b&eacute;. Sản phẩm&nbsp;an to&agrave;n kh&ocirc;ng g&acirc;y k&